INSERT INTO `sc_goods` VALUES ('e0fb8fdcfa94', 'b3347294bd57', 'c5c05b29ee4c', '玛卡', 1, '2018-10-13 19:18:15', '2019-03-07 14:49:05', '', 'niuhao', '先购先得', '/scimages/20181013/c5c05b29ee4c/goods/20181013191813_99.jpg', '/scimages/20181013/c5c05b29ee4c/goods/20181013191807_132.jpg', '玛卡干根中的化学组成成份：蛋白质含量为10%以上（胡宁湖畔的玛卡品种的蛋白质含量超过14%），59%的碳水化合物；8.5%的纤维，内含丰富的锌、钙、铁、钛、铷、钾、钠、铜、锰、镁、锶、磷、碘等矿物质，并含有维生素C、B1、B2、B6、A、E、B12、B5 ，脂肪含量不高但其中多为不饱和脂肪酸，亚油酸和亚麻酸的含量达53%以上，天然活性成份包括生物碱、芥子油苷及其分解产物异硫氰酸苄酯、甾醇、多酚类物质等。', '元/斤', 110.00);
INSERT INTO `sc_goods` VALUES ('e8340a22227a', 'b3347294bd57', 'c5c05b29ee4c', '辣木籽', 3, '2018-10-26 17:31:33', '2018-11-22 11:38:05', '', 'niuhao', '暂无', '/scimages/20181026/c5c05b29ee4c/goods/20181026173044_790.png', '/scimages/20181026/c5c05b29ee4c/goods/20181026173131_846.png', '辣木籽的种子和叶子中含有丰富的营养成分，但辣木籽本身含有生物碱、辣木黄酮 ，高血压、糖尿病患者及孕妇等最好经医师指示后酌量食用。辣木籽要剥壳吃，壳可以泡水，每天吃5-8粒，每次吃完喝300-500毫升的温开水，因为其营养丰富，需要水来分解，否则会口干。', '元/斤', 50.00);
