INSERT INTO `t_advertising` VALUES (99, 'c5c05b29ee4c', '&lt;p&gt;\n	&lt;img src=\"/images22/c5c05b29ee4c/20190313/20190313150149_745.jpg\" alt=\"0000\" width=\"200\" height=\"114\" title=\"0000\" align=\"\" /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n&lt;pre class=\"prettyprint linenums lang-xml\"&gt;server:\n  port: 443\n  tomcat:\n    #uri-encoding: utf-8\n    #手动的将临时文件夹设置为自定义的文件夹，就不会被Linux删除了\n    basedir: /root/temp\n    #max-http-header-size: 8192\n  ssl:\n    enabled: true\n    key-store: classpath:1791425.pfx\n    key-store-password: y15F56bX\n    keyStoreType: PKCS12&lt;/pre&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;package cc.mrbird.job.util;\n\nimport cc.mrbird.common.util.SpringContextUtils;\nimport cc.mrbird.job.domain.Job;\nimport cc.mrbird.job.domain.JobLog;\nimport cc.mrbird.job.service.JobLogService;\nimport org.apache.commons.lang3.StringUtils;\nimport org.quartz.JobExecutionContext;\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\nimport org.springframework.scheduling.quartz.QuartzJobBean;\n\nimport java.util.Date;\nimport java.util.concurrent.ExecutorService;\nimport java.util.concurrent.Executors;\nimport java.util.concurrent.Future;\n\n/**\n * 定时任务\n *\n * @author MrBird\n */\npublic class ScheduleJob extends QuartzJobBean {\n    private Logger logger = LoggerFactory.getLogger(this.getClass());\n    private ExecutorService service = Executors.newSingleThreadExecutor();\n\n    @Override\n    protected void executeInternal(JobExecutionContext context) {\n        Job scheduleJob = (Job) context.getMergedJobDataMap().get(Job.JOB_PARAM_KEY);\n\n        // 获取spring bean\n        JobLogService scheduleJobLogService = (JobLogService) SpringContextUtils.getBean(\"JobLogService\");\n\n        JobLog log = new JobLog();\n        log.setJobId(scheduleJob.getJobId());\n        log.setBeanName(scheduleJob.getBeanName());\n        log.setMethodName(scheduleJob.getMethodName());\n        log.setParams(scheduleJob.getParams());\n        log.setCreateTime(new Date());\n\n        long startTime = System.currentTimeMillis();\n\n        try {\n            // 执行任务\n            logger.info(\"任务准备执行，任务ID：{}\", scheduleJob.getJobId());\n            ScheduleRunnable task = new ScheduleRunnable(scheduleJob.getBeanName(), scheduleJob.getMethodName(),\n                    scheduleJob.getParams());\n            Future&amp;lt;?&amp;gt; future = service.submit(task);\n            future.get();\n            long times = System.currentTimeMillis() - startTime;\n            log.setTimes(times);\n            // 任务状态 0：成功 1：失败\n            log.setStatus(\"0\");\n\n            logger.info(\"任务执行完毕，任务ID：{} 总共耗时：{} 毫秒\", scheduleJob.getJobId(), times);\n        } catch (Exception e) {\n            logger.error(\"任务执行失败，任务ID：\" + scheduleJob.getJobId(), e);\n            long times = System.currentTimeMillis() - startTime;\n            log.setTimes(times);\n            // 任务状态 0：成功 1：失败\n            log.setStatus(\"1\");\n            log.setError(StringUtils.substring(e.toString(), 0, 2000));\n        } finally {\n            scheduleJobLogService.saveJobLog(log);\n        }\n    }\n}\n&lt;/pre&gt;\n&lt;/p&gt;', '2019-03-07 18:19:11', '2019-03-13 15:03:41', 'admin', '2222', '', '2222', 9, 1);
INSERT INTO `t_advertising` VALUES (100, 'c5c05b29ee4c', '&lt;p style=\"text-indent:2em;\"&gt;	3月8日消息，昨日晚间，有用户发现“熊猫直播”APP 已在苹果 App Store 下架，只能搜到“熊猫主播版”，除此之外，谷歌应用商店也已经下架了该应用，不过国内的大部分安卓应用市场还未下架，目前仍可正常下载。&lt;/p&gt;&lt;p style=\"text-indent:2em;\"&gt;	虽然应用已经开始下架，但目前“熊猫直播”的官网仍可正常打开，在其官方主页上还可以看到一些主播在向粉丝们告别，侧面的印证了“熊猫直播”要倒闭的传言，正式进入破产倒计时。&lt;/p&gt;&lt;div&gt;	&lt;img src=\"/images22/c5c05b29ee4c/20190308/20190308151430_861.jpeg\" alt=\"\" /&gt;&lt;br /&gt;&lt;/div&gt;&lt;p style=\"text-indent:2em;\"&gt;	据资料显示，熊猫直播是一款主打“游戏直播”的直播平台，该应用于 2015 年 10 月 21 日正式上线运营，国民老公“王思聪”为该公司的创始人及 CEO，这款软件除了游戏直播外，还包括各种娱乐直播、户外直播以及综艺直播等，被定位为“泛娱乐”直播平台。&lt;/p&gt;&lt;p style=\"text-indent:2em;\"&gt;	凭借独特的直播领域和王思聪的影响力，熊猫直播在上线不到一年的时间就完成了三轮融资，2015 年 11 月熊猫直播完成了首笔数百万的天使轮融资，2016 年 9 月份再次完成 6.5 亿元 A 轮融资，而到了 2017 年 5 月更是完成了高达 10 亿元的 B 轮融资，这也是熊猫直播的最后一次融资。&lt;/p&gt;&lt;div&gt;	&lt;img src=\"/images22/c5c05b29ee4c/20190308/20190308151443_728.jpeg\" alt=\"\" /&gt;&lt;br /&gt;&lt;/div&gt;&lt;p style=\"text-indent:2em;\"&gt;	不久前，有消息称“熊猫直播”已经进入破产清算阶段，熊猫直播的资产已经被冻结，员工也已经被遣散，联合创始人及众多高管均已离职，同时平台服务器也将会在 3 月 18 日正式关闭，除此之外还有主播表示平台目前还拖欠公会的钱。&lt;/p&gt;&lt;p style=\"text-indent:2em;\"&gt;	对于传言，熊猫直播官方并没有出面辟谣，说明这件事情并非空穴来风，如果熊猫直播倒闭属实，那么员工可能真的得不到“遣散费”，或许最终将由王思聪掏钱解决。&lt;/p&gt;', '2019-03-08 15:14:45', '2019-03-08 15:15:35', 'admin', '熊猫直播破产倒计时，APP 已在苹果 App Store 下架', '', '熊猫直播破产倒计时，APP 已在苹果 App Store 下架', 10, 9);
INSERT INTO `t_advertising` VALUES (101, 'c5c05b29ee4c', '&lt;h3 id=\"1\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:1.25em;margin-left:0px;background-color:#FFFFFF;\"&gt;\n	一、键值设计\n&lt;/h3&gt;\n&lt;h4 id=\"2\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	1. key名设计\n&lt;/h4&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;(1)【建议】: 可读性和可管理性&lt;/span&gt;\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	以业务名(或数据库名)为前缀(防止key冲突)，用冒号分隔，比如业务名:表名:id\n&lt;/p&gt;\n&lt;pre&gt;&lt;span class=\"hljs-selector-tag\" style=\"color:#F92672;\"&gt;ugc&lt;/span&gt;&lt;span class=\"hljs-selector-pseudo\" style=\"color:#E6DB74;\"&gt;:video&lt;/span&gt;&lt;span class=\"hljs-selector-pseudo\" style=\"color:#E6DB74;\"&gt;:1&lt;/span&gt;&lt;/pre&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;(2)【建议】：简洁性&lt;/span&gt;\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	保证语义的前提下，控制key的长度，当key较多时，内存占用也不容忽视，例如：\n&lt;/p&gt;\n&lt;pre&gt;&lt;span class=\"hljs-symbol\" style=\"color:#66D9EF;\"&gt; \n&lt;pre class=\"prettyprint linenums lang-js\"&gt;user:{uid}:friends:messages:{mid}简化为u:{uid}:fr:m:{mid}。&lt;/pre&gt;\n&lt;/span&gt;&lt;/pre&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		(3)【强制】：不要包含特殊字符\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	反例：包含空格、换行、单双引号以及其他转义字符\n&lt;/p&gt;\n&lt;h4 id=\"3\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	2. value设计\n&lt;/h4&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		(1)【强制】：拒绝bigkey(防止网卡流量、慢查询)\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	string类型控制在10KB以内，hash、list、set、zset元素个数不要超过5000。\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	反例：一个包含200万个元素的list。\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	非字符串的bigkey，不要使用del删除，使用hscan、sscan、zscan方式渐进式删除，同时要注意防止bigkey过期时间自动删除问题(例如一个200万的zset设置1小时过期，会触发del操作，造成阻塞，而且该操作不会不出现在慢查询中(latency可查))，&lt;a href=\"https://yq.aliyun.com/articles/531067#cc1\"&gt;查找方法&lt;/a&gt;和&lt;a href=\"https://yq.aliyun.com/articles/531067#cc2\"&gt;删除方法&lt;/a&gt; \n&lt;/p&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		(2)【推荐】：选择适合的数据类型。\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	例如：实体类型(要合理控制和使用数据结构内存编码优化配置,例如ziplist，但也要注意节省内存和性能之间的平衡)\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	反例：\n&lt;/p&gt;\n&lt;pre&gt;&lt;span class=\"hljs-keyword\" style=\"color:#F92672;\"&gt; \n&lt;pre class=\"prettyprint linenums lang-js\"&gt;set user:1:name tom set user:1:age 19 set user:1:favor football&lt;/pre&gt;\n&lt;/span&gt;&lt;/pre&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	正例:\n&lt;/p&gt;\n&lt;pre&gt;&lt;span class=\"hljs-selector-tag\" style=\"color:#F92672;\"&gt; \n&lt;pre class=\"prettyprint linenums\"&gt;hmset user:1 name tom age 19 favor football\n&lt;/pre&gt;\n&lt;/span&gt;&lt;/pre&gt;\n&lt;h4 id=\"4\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	3.【推荐】：控制key的生命周期，redis不是垃圾桶。\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	建议使用expire设置过期时间(条件允许可以打散过期时间，防止集中过期)，不过期的数据重点关注idletime。\n&lt;/p&gt;\n&lt;h3 id=\"5\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:1.25em;background-color:#FFFFFF;\"&gt;\n	二、命令使用\n&lt;/h3&gt;\n&lt;h4 id=\"6\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	1.【推荐】 O(N)命令关注N的数量\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	例如hgetall、lrange、smembers、zrange、sinter等并非不能使用，但是需要明确N的值。有遍历的需求可以使用hscan、sscan、zscan代替。\n&lt;/p&gt;\n&lt;h4 id=\"7\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	2.【推荐】：禁用命令\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	禁止线上使用keys、flushall、flushdb等，通过redis的rename机制禁掉命令，或者使用scan的方式渐进式处理。\n&lt;/p&gt;\n&lt;h4 id=\"8\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	3.【推荐】合理使用select\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	redis的多数据库较弱，使用数字进行区分，很多客户端支持较差，同时多业务用多数据库实际还是单线程处理，会有干扰。\n&lt;/p&gt;\n&lt;h4 id=\"9\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	4.【推荐】使用批量操作提高效率\n&lt;/h4&gt;\n&lt;pre&gt;原生命令：例如mget、mset。\n非原生命令：可以使用pipeline提高效率。&lt;/pre&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	但要注意控制一次批量操作的&lt;span style=\"font-weight:600;\"&gt;元素个数&lt;/span&gt;(例如500以内，实际也和元素字节数有关)。\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	注意两者不同：\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;1. 原生是原子操作，pipeline是非原子操作。 2. pipeline可以打包不同的命令，原生做不到 3. pipeline需要客户端和服务端同时支持。\n&lt;/pre&gt;\n&lt;p&gt;\n	5.【建议】Redis事务功能较弱，不建议过多使用\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	Redis的事务功能较弱(不支持回滚)，而且集群版本(自研和官方)要求一次事务操作的key必须在一个slot上(可以使用hashtag功能解决)\n&lt;/p&gt;\n&lt;h4 id=\"11\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	6.【建议】Redis集群版本在使用Lua上有特殊要求：\n&lt;/h4&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		1.所有key都应该由 KEYS 数组来传递，redis.call/pcall 里面调用的redis命令，key的位置，必须是KEYS array, 否则直接返回error，\"-ERR bad lua script for redis cluster, all the keys that the script uses should be passed using the KEYS array\"\n	&lt;/li&gt;\n	&lt;li&gt;\n		2.所有key，必须在1个slot上，否则直接返回error, \"-ERR eval/evalsha command keys must in same slot\"\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;h4 id=\"12\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	7.【建议】必要情况下使用monitor命令时，要注意不要长时间使用。\n&lt;/h4&gt;\n&lt;h3 id=\"13\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:1.25em;background-color:#FFFFFF;\"&gt;\n	三、客户端使用\n&lt;/h3&gt;\n&lt;h4 id=\"14\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	1.【推荐】\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	避免多个应用使用一个Redis实例\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	正例：不相干的业务拆分，公共数据做服务化。\n&lt;/p&gt;\n&lt;h4 id=\"15\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	2.【推荐】\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	使用带有连接池的数据库，可以有效控制连接，同时提高效率，标准使用方式：\n&lt;/p&gt;\n&lt;pre&gt;执行命令如下：\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;Jedis jedis = null; try {\n    jedis = jedisPool.getResource(); //具体的命令 jedis.executeCommand()\n} catch (Exception e) {\n    logger.error(\"op key {} error: \" + e.getMessage(), key, e);\n} finally { //注意这里不是关闭连接，在JedisPool模式下，Jedis会被归还给资源池。 if (jedis != null) \n        jedis.close();\n}&lt;/pre&gt;\n&lt;br /&gt;\n&lt;/pre&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	下面是JedisPool优化方法的文章:\n&lt;/p&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		&lt;a href=\"https://yq.aliyun.com/articles/236384\"&gt;Jedis常见异常汇总&lt;/a&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;a href=\"https://yq.aliyun.com/articles/236383\"&gt;JedisPool资源池优化&lt;/a&gt; \n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;h4 id=\"16\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	3.【建议】\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	高并发下建议客户端添加熔断功能(例如netflix hystrix)\n&lt;/p&gt;\n&lt;h4 id=\"17\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	4.【推荐】\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	设置合理的密码，如有必要可以使用SSL加密访问（阿里云Redis支持）\n&lt;/p&gt;\n&lt;h4 id=\"18\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	5.【建议】\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	根据自身业务类型，选好maxmemory-policy(最大内存淘汰策略)，设置好过期时间。\n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	默认策略是volatile-lru，即超过最大内存后，在过期键中使用lru算法进行key的剔除，保证不过期数据不被删除，但是可能会出现OOM问题。\n&lt;/p&gt;\n&lt;h5 id=\"19\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:0.875em;background-color:#FFFFFF;\"&gt;\n	其他策略如下：\n&lt;/h5&gt;\n&lt;ul style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;li&gt;\n		allkeys-lru：根据LRU算法删除键，不管数据有没有设置超时属性，直到腾出足够空间为止。\n	&lt;/li&gt;\n	&lt;li&gt;\n		allkeys-random：随机删除所有键，直到腾出足够空间为止。\n	&lt;/li&gt;\n	&lt;li&gt;\n		volatile-random:随机删除过期键，直到腾出足够空间为止。\n	&lt;/li&gt;\n	&lt;li&gt;\n		volatile-ttl：根据键值对象的ttl属性，删除最近将要过期数据。如果没有，回退到noeviction策略。\n	&lt;/li&gt;\n	&lt;li&gt;\n		noeviction：不会剔除任何数据，拒绝所有写入操作并返回客户端错误信息\"(error) OOM command not allowed when used memory\"，此时Redis只响应读操作。\n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;h3 id=\"20\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:1.25em;background-color:#FFFFFF;\"&gt;\n	四、相关工具\n&lt;/h3&gt;\n&lt;h4 id=\"21\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	1.【推荐】：数据同步\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	redis间数据同步可以使用：redis-port\n&lt;/p&gt;\n&lt;h4 id=\"22\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	2.【推荐】：big key搜索\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;a href=\"https://yq.aliyun.com/articles/117042\"&gt;redis大key搜索工具&lt;/a&gt; \n&lt;/p&gt;\n&lt;h4 id=\"23\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	3.【推荐】：热点key寻找(内部实现使用monitor，所以建议短时间使用)\n&lt;/h4&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	&lt;a href=\"https://github.com/facebookarchive/redis-faina\"&gt;facebook的redis-faina&lt;/a&gt; \n&lt;/p&gt;\n&lt;p style=\"color:#24292E;font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:16px;background-color:#FFFFFF;\"&gt;\n	阿里云Redis已经在内核层面解决热点key问题，欢迎使用。\n&lt;/p&gt;\n&lt;h2 id=\"24\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:18px;background-color:#FFFFFF;\"&gt;\n	五 附录：删除bigkey\n&lt;/h2&gt;\n&lt;pre&gt;&lt;span class=\"hljs-bullet\" style=\"color:#AE81FF;\"&gt; \n&lt;pre class=\"prettyprint linenums\"&gt;1. 下面操作可以使用pipeline加速。 2. redis 4.0已经支持key的异步删除，欢迎使用。\n&lt;/pre&gt;\n&lt;/span&gt;&lt;/pre&gt;\n&lt;h5 id=\"25\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:0.875em;background-color:#FFFFFF;\"&gt;\n	1. Hash删除: hscan + hdel\n&lt;/h5&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;public void delBigHash(String host, int port, String password, String bigHashKey) {\n    Jedis jedis = new Jedis(host, port); if (password != null &amp;amp;&amp;amp; !\"\".equals(password)) {\n        jedis.auth(password);\n    }\n    ScanParams scanParams = new ScanParams().count(100); String cursor = \"0\"; do {\n        ScanResult&amp;lt;Entry&amp;lt;String, String&amp;gt;&amp;gt; scanResult = jedis.hscan(bigHashKey, cursor, scanParams);\n        List&amp;lt;Entry&amp;lt;String, String&amp;gt;&amp;gt; entryList = scanResult.getResult(); if (entryList != null &amp;amp;&amp;amp; !entryList.isEmpty()) { for (Entry&amp;lt;String, String&amp;gt; entry : entryList) {\n                jedis.hdel(bigHashKey, entry.getKey());\n            }\n        }\n        cursor = scanResult.getStringCursor();\n    } while (!\"0\".equals(cursor)); //删除bigkey jedis.del(bigHashKey);\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;h5 id=\"26\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:0.875em;background-color:#FFFFFF;\"&gt;\n	2. List删除: ltrim\n&lt;/h5&gt;\n&lt;pre&gt;\n\n\n\n\n\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;public void delBigList(String host, int port, String password, String bigListKey) {\n    Jedis jedis = new Jedis(host, port); if (password != null &amp;amp;&amp;amp; !\"\".equals(password)) {\n        jedis.auth(password);\n    } long llen = jedis.llen(bigListKey); int counter = 0; int left = 100; while (counter &amp;lt; llen) { //每次从左侧截掉100个 jedis.ltrim(bigListKey, left, llen);\n        counter += left;\n    } //最终删除key jedis.del(bigListKey);\n}&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;h5 id=\"27\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:0.875em;background-color:#FFFFFF;\"&gt;\n	3. Set删除: sscan + srem\n&lt;/h5&gt;\n&lt;pre&gt;\n\n\n\n\n\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;public void delBigSet(String host, int port, String password, String bigSetKey) {\n    Jedis jedis = new Jedis(host, port); if (password != null &amp;amp;&amp;amp; !\"\".equals(password)) {\n        jedis.auth(password);\n    }\n    ScanParams scanParams = new ScanParams().count(100); String cursor = \"0\"; do {\n        ScanResult&amp;lt;String&amp;gt; scanResult = jedis.sscan(bigSetKey, cursor, scanParams);\n        List&amp;lt;String&amp;gt; memberList = scanResult.getResult(); if (memberList != null &amp;amp;&amp;amp; !memberList.isEmpty()) { for (String member : memberList) {\n                jedis.srem(bigSetKey, member);\n            }\n        }\n        cursor = scanResult.getStringCursor();\n    } while (!\"0\".equals(cursor)); //删除bigkey jedis.del(bigSetKey);\n}&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;h5 id=\"28\" style=\"font-family:-apple-system, BlinkMacSystemFont, &amp;quot;font-size:0.875em;background-color:#FFFFFF;\"&gt;\n	4. SortedSet删除: zscan + zrem\n&lt;/h5&gt;\n&lt;pre&gt;\n\n\n\n\n\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;public void delBigZset(String host, int port, String password, String bigZsetKey) {\n    Jedis jedis = new Jedis(host, port); if (password != null &amp;amp;&amp;amp; !\"\".equals(password)) {\n        jedis.auth(password);\n    }\n    ScanParams scanParams = new ScanParams().count(100); String cursor = \"0\"; do {\n        ScanResult&amp;lt;Tuple&amp;gt; scanResult = jedis.zscan(bigZsetKey, cursor, scanParams);\n        List&amp;lt;Tuple&amp;gt; tupleList = scanResult.getResult(); if (tupleList != null &amp;amp;&amp;amp; !tupleList.isEmpty()) { for (Tuple tuple : tupleList) {\n                jedis.zrem(bigZsetKey, tuple.getElement());\n            }\n        }\n        cursor = scanResult.getStringCursor();\n    } while (!\"0\".equals(cursor)); //删除bigkey jedis.del(bigZsetKey);\n}&lt;/pre&gt;\n&lt;span&gt;&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/pre&gt;', '2019-03-11 15:17:44', '2019-03-13 14:39:12', 'admin', '阿里云Redis开发规范', '', '阿里云Redis开发规范', 4, 1);
INSERT INTO `t_advertising` VALUES (102, 'c5c05b29ee4c', '&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	通过深入分析Spring源码，我们知道Spring框架包括大致六大模块， 如Web模块，数据库访问技术模块，面向切面模块，基础设施模块，核心容器模块和模块，\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	其中，在Spring框架的Web模块中，又包含很多前端技术，如SpringMVC,Spring WebSocket,Spring WebPortlet等，在本篇文章中，我们主要分析SpringMVC模块，\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	在分析SpringMVC技术时，本篇文章将通过解读SpringMVC源码，并通过编译器断点调试追踪，研究Spring核心类的UML图，来深入剖析SpringMVC的执行过程。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;strong&gt;&lt;span style=\"font-size:16px;\"&gt;一&amp;nbsp; Spring核心模块&lt;/span&gt;&lt;/strong&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	下图为Spring核心模块，其中SpringMVC模块位于Spring框架的Web模块部分\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190307172308277-62474723.png\" alt=\"\" width=\"666\" height=\"478\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;strong&gt;&lt;span style=\"font-size:16px;\"&gt;二&amp;nbsp; SpringMVC执行原理&lt;/span&gt;&lt;/strong&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	下图为SpringMVC的一个较完整的流程图，实线表示SpringMVC框架提供的技术，不需要开发者实现，虚线表示需要开发者实现。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	简要分析执行流程：\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	1.DispatcherServlet表示前置控制器，是整个SpringMVC的控制中心。用户发出请求，DispatcherServlet接收请求并拦截请求，\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	我们假设请求的url为:\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	http://localhost:9999/SpringMVC/input-product\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	如上url拆分成三部分：\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	(1)http://localhost:9999 服务器域名\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	(2)SpringMVC部署在服务器(http://localhost:9999)上的web站点\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	(3)input-product表示控制器\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	通过分析，如上url表示为：请求位于服务器localhost:9999上的SpringMVC站点的input-product控制器\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	2.HandlerMapping为处理器映射。DispatcherServlet调用HandlerMapping,HandlerMapping根据请求url查找Handler\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	3.HandlerExecution表示具体的Handler,其主要作用是根据url查找控制器，如上url被查找控制器为：input-product\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	4.HandlerExecution将解析后的信息传递给DispatcherServlet,如解析控制器映射等\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	5.HandlerAdapter表示处理器适配器，其按照特定的规则去执行Handler\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	6.Handler让具体的Controller执行\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	7.Controller将具体的执行信息返回给HandlerAdapter,如ModelAndView\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	8.HandlerAdapter将视图逻辑名或模型传递给DispatcherServlet\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	9.DispatcherServlet调用视图解析器(ViewResolver)来解析HandlerAdapter传递的逻辑视图名\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	10.视图解析器将解析的逻辑视图名传给DispatcherServlet\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	11.DispatcherServlet根据视图解析器解析的视图结果，调用具体的视图\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	12.最终视图呈现给用户。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190307172453210-409890354.png\" alt=\"\" width=\"804\" height=\"421\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;span style=\"font-size:16px;\"&gt;&lt;strong&gt;三&amp;nbsp; SpringMVC核心组件&lt;/strong&gt;&lt;/span&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;（一）DispatcherServlet\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	DispatcherServlet就是所谓的SpringMVC前端控制器，作为整个SpringMVC的控制中心,，其相关UML如下所示：\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190307181328225-1500109594.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;（二）HandlerMapping\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	HandlerMapping主要用来解析请求url，解析出控制器，从而映射控制器，其相关类有16个。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308011205111-1334862023.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	如下为HandlerMapping的UML图\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308012521101-1794489256.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;（三）HandlerAdapter\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	HandlerAdapter主要是调度Controller来处理业务逻辑等，想相关类有6个。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308012247068-188786650.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	如下为HandlerAdapter的UML：\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308012201932-690732571.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;（四）ViewResolver\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	ViewResolver接口主要作用是解析DispatcherServlet传递的逻辑视图名，并将解析结果传回给DispatcherServlet。实现该接口的相关类或接口共有19个。\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308012737248-1101227503.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&amp;nbsp;如下为ViewResolver主要的UML:\n&lt;/p&gt;\n&lt;p style=\"font-family:Verdana, Arial, Helvetica, sans-serif;background-color:#FFFFFF;\"&gt;\n	&lt;img src=\"https://img2018.cnblogs.com/blog/1066923/201903/1066923-20190308015607265-1155748156.png\" alt=\"\" style=\"height:auto;\" /&gt;\n&lt;/p&gt;', '2019-03-11 15:29:15', '2019-03-13 12:13:47', 'niuhao', '浅谈SpringMVC执行过程', '', '浅谈SpringMVC执行过程', 1, 1);
INSERT INTO `t_advertising` VALUES (103, 'c5c05b29ee4c', '&lt;p&gt;\n	&lt;strong&gt;算法思想&lt;/strong&gt;：对相邻的元素进行两两比较，顺序相反则进行交换，这样，每一趟会将最小或最大的元素“浮”到顶端，最终达到完全有序。\n&lt;/p&gt;\n&lt;h3&gt;\n	原始冒泡\n&lt;/h3&gt;\n&lt;p&gt;\n	相邻元素比较，大数后沉小数前移\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums\"&gt;def bubble_sort(items):\n     \"\"\"冒泡排序 - O(N**2) \"\"\"\n     for i in range(1, len(items)):\n         for j in range(0, len(items) - i):\n             if items[j] &amp;gt; items[j + 1]:\n                 items[j], items[j + 1] = items[j + 1], items[j]&lt;/pre&gt;\n&lt;h3&gt;\n	优化一（判断对象有序，提前结束循环）\n&lt;/h3&gt;\n&lt;p&gt;\n	类似于：[9, 1, 2, 3, 4, 5, 6, 7, 8] 这样的部分有序待排序数组，使用原始冒泡发现除了第一次，之后均为无效操作。观察发现，当一次比较之后，如果没有发生交换行为，则认为该对象已经有序，便可提前结束循环。\n&lt;/p&gt;\n&lt;pre class=\"hljs python\"&gt;\n&lt;pre class=\"prettyprint linenums\"&gt;def bubble_sort(items):\n     \"\"\"冒泡排序 - O(N**2) \"\"\"\n     for i in range(1, len(items)):\n         for j in range(0, len(items) - i):\n             swapped = False\n             if items[j] &amp;gt; items[j + 1]:\n                 items[j], items[j + 1] = items[j + 1], items[j]\n                 swapped = True\n         if not swapped:\n             break&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;h3&gt;\n	优化二（解\'&amp;gt;\'的耦合，扩大函数使用范围）\n&lt;/h3&gt;\n&lt;p&gt;\n	类似于 {\'Lucy\'：20，\'Hope\':18, \'Anna\':23, \'Tom\':66} 的数据经过方式一优化的冒泡仍然不能适用。究其原因是由于判断时运算符耦合所引起的参数对象类型错误，通过隐函数的方式解耦合即可解决。（参数说明：*之前的为位置参数，之后的为命名关键字参数）\n&lt;/p&gt;\n&lt;pre class=\"hljs python\"&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;def bubble_sort(origin_items, *, comp=lambda x, y: x &amp;gt; y):\n     \"\"\"冒泡排序 - O(N**2) \"\"\"\n     for i in range(1, len(items)):\n         for j in range(0, len(items) - i):\n             swapped = False\n             if comp(items[j], items[j + 1]):\n                 items[j], items[j + 1] = items[j + 1], items[j]\n                 swapped = True\n         if not swapped:\n             break&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;h3&gt;\n	优化三（创建函数对象，避免对调用函数对象的修改）\n&lt;/h3&gt;\n&lt;p&gt;\n	观察Python内置的排序算法（sort：&lt;a href=\"https://www.cnblogs.com/clement-jiao/p/9243066.html\" target=\"_blank\"&gt;Timesort实现&lt;/a&gt;）发现调用函数本身不对对象进行修改，而是返回一个重新生成的对象，保留调用函数的对象。故而通过切片操作实现函数对象的生成。\n&lt;/p&gt;\n&lt;pre class=\"hljs python\"&gt;\n\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;def bubble_sort(origin_items, *, comp=lambda x, y: x &amp;gt; y):\n     \"\"\"冒泡排序 - O(N**2) \"\"\"\n     items = origin_items[:]\n     for i in range(1, len(items)):\n         for j in range(0, len(items) - i):\n             swapped = False\n             if comp(items[j], items[j + 1]):\n                 items[j], items[j + 1] = items[j + 1], items[j]\n                 swapped = True\n         if not swapped:\n             break\n    return items&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;h3&gt;\n	优化四（双向比较，又称搅拌排序、鸡尾酒排序）\n&lt;/h3&gt;\n&lt;p&gt;\n	类似于 [9, 2, 3, 4, 5, 6, 7, 8, 1] 这样的待排序对象，可以看出如果正向依次排序之后再反向进行相邻比较，只需要两次之后，第三次即可根据判断有无发生交换提前结束循环，大大减小了运算的时间。\n&lt;/p&gt;\n&lt;pre class=\"hljs python\"&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;def bubble_sort(origin_items, *, comp=lambda x, y: x &amp;gt; y,  comp_back=lambda x, y: x &amp;lt; y,):\n     \"\"\"冒泡排序 - O(N**2) \"\"\"\n     items = origin_items[:]\n     for i in range(1, len(items)):\n         swapped = False\n         for j in range(0, len(items) - i):\n             if comp(items[j], items[j + 1]):\n                 items[j], items[j + 1] = items[j + 1], items[j]\n                 swapped = True\n         for j in range( len(items) - i, 0, -1):\n             if comp_back(items[j], items[j - 1]):\n                 items[j], items[j - 1] = items[j - 1], items[j]\n                 swapped = True\n         if not swapped:\n             break\n    return items&lt;/pre&gt;\n&lt;/pre&gt;\n&lt;p&gt;\n	简单排序（时间复杂度为O(N**2)的排序算法）中对冒泡排序面试面的最多，大概就是冒泡算法容易优化吧！而且优化之后也是相当便捷的o(*￣︶￣*)o\n&lt;/p&gt;\n&lt;br /&gt;\n作者：GHope&lt;br /&gt;\n链接：https://www.jianshu.com/p/f74fdcb2aa0c&lt;br /&gt;', '2019-03-12 18:58:39', '2019-03-13 17:14:27', 'niuhao', '冒泡排序的优化', '', '冒泡排序的优化', 3, 1);
INSERT INTO `t_advertising` VALUES (104, 'c5c05b29ee4c', '&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;1、对查询进行优化，应尽量避免全表扫描，首先应考虑在 where 及 order by 涉及的列上建立索引。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;2、应尽量避免在 where 子句中使用!=或&amp;lt;&amp;gt;操作符，否则将引擎放弃使用索引而进行全表扫描。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;3、应尽量避免在 where 子句中对字段进行 null 值判断，否则将导致引擎放弃使用索引而进行全表扫描，如：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num is null&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;可以在num上设置默认值0，确保表中num列没有null值，然后这样查询：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=0&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;4、应尽量避免在 where 子句中使用 or 来连接条件，否则将导致引擎放弃使用索引而进行全表扫描，如：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=10 or num=20&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;可以这样查询：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=10&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;union all&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=20&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;5、下面的查询也将导致全表扫描：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where name like \'%abc%\'&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;若要提高效率，可以考虑全文检索。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;6、in 和 not in 也要慎用，否则会导致全表扫描，如：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num in(1,2,3)&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;对于连续的数值，能用 between 就不要用 in 了：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num between 1 and 3&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;7、如果在 where 子句中使用参数，也会导致全表扫描。因为SQL只有在运行时才会解析局部变量，但优化程序不能将访问计划的选择推迟到运行时；它必须在编译时进行选择。然而，如果在编译时建立访问计划，变量的值还是未知的，因而无法作为索引选择的输入项。如下面语句将进行全表扫描：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=@num&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;可以改为强制查询使用索引：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t with(index(索引名)) where num=@num&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;8、应尽量避免在 where 子句中对字段进行表达式操作，这将导致引擎放弃使用索引而进行全表扫描。如：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num/2=100&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;应改为:&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where num=100*2&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;9、应尽量避免在where子句中对字段进行函数操作，这将导致引擎放弃使用索引而进行全表扫描。如：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where substring(name,1,3)=\'abc\'--name以abc开头的id&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where datediff(day,createdate,\'2005-11-30\')=0--\'2005-11-30\'生成的id&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;应改为:&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where name like \'abc%\'&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select id from t where createdate&amp;gt;=\'2005-11-30\' and createdate&amp;lt;\'2005-12-1\'&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;10、不要在 where 子句中的“=”左边进行函数、算术运算或其他表达式运算，否则系统将可能无法正确使用索引。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;11、在使用索引字段作为条件时，如果该索引是复合索引，那么必须使用到该索引中的第一个字段作为条件时才能保证系统使用该索引，否则该索引将不会被使用，并且应尽可能的让字段顺序与索引顺序相一致。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;12、不要写一些没有意义的查询，如需要生成一个空表结构：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select col1,col2 into #t from t where 1=0&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;这类代码不会返回任何结果集，但是会消耗系统资源的，应改成这样：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;create table #t(...)&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;13、很多时候用 exists 代替 in 是一个好的选择：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select num from a where num in(select num from b)&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;用下面的语句替换：&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;select num from a where exists(select 1 from b where num=a.num)&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;14、并不是所有索引对查询都有效，SQL是根据表中数据来进行查询优化的，当索引列有大量数据重复时，SQL查询可能不会去利用索引，如一表中有字段sex，male、female几乎各一半，那么即使在sex上建了索引也对查询效率起不了作用。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;15、索引并不是越多越好，索引固然可以提高相应的 select 的效率，但同时也降低了 insert 及 update 的效率，因为 insert 或 update 时有可能会重建索引，所以怎样建索引需要慎重考虑，视具体情况而定。一个表的索引数最好不要超过6个，若太多则应考虑一些不常使用到的列上建的索引是否有必要。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;16、应尽可能的避免更新 clustered 索引数据列，因为 clustered 索引数据列的顺序就是表记录的物理存储顺序，一旦该列值改变将导致整个表记录的顺序的调整，会耗费相当大的资源。若应用系统需要频繁更新 clustered 索引数据列，那么需要考虑是否应将该索引建为 clustered 索引。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;17、尽量使用数字型字段，若只含数值信息的字段尽量不要设计为字符型，这会降低查询和连接的性能，并会增加存储开销。这是因为引擎在处理查询和连接时会逐个比较字符串中每一个字符，而对于数字型而言只需要比较一次就够了。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;18、尽可能的使用 varchar/nvarchar 代替 char/nchar ，因为首先变长字段存储空间小，可以节省存储空间，其次对于查询来说，在一个相对较小的字段内搜索效率显然要高些。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;19、任何地方都不要使用 select * from t ，用具体的字段列表代替“*”，不要返回用不到的任何字段。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;20、尽量使用表变量来代替临时表。如果表变量包含大量数据，请注意索引非常有限（只有主键索引）。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;21、避免频繁创建和删除临时表，以减少系统表资源的消耗。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;22、临时表并不是不可使用，适当地使用它们可以使某些例程更有效，例如，当需要重复引用大型表或常用表中的某个数据集时。但是，对于一次性事件，最好使用导出表。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;23、在新建临时表时，如果一次性插入数据量很大，那么可以使用 select into 代替 create table，避免造成大量 log ，以提高速度；如果数据量不大，为了缓和系统表的资源，应先create table，然后insert。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;24、如果使用到了临时表，在存储过程的最后务必将所有的临时表显式删除，先 truncate table ，然后 drop table ，这样可以避免系统表的较长时间锁定。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;25、尽量避免使用游标，因为游标的效率较差，如果游标操作的数据超过1万行，那么就应该考虑改写。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;26、使用基于游标的方法或临时表方法之前，应先寻找基于集的解决方案来解决问题，基于集的方法通常更有效。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;27、与临时表一样，游标并不是不可使用。对小型数据集使用 FAST_FORWARD 游标通常要优于其他逐行处理方法，尤其是在必须引用几个表才能获得所需的数据时。在结果集中包括“合计”的例程通常要比使用游标执行的速度快。如果开发时间允许，基于游标的方法和基于集的方法都可以尝试一下，看哪一种方法的效果更好。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;28、在所有的存储过程和触发器的开始处设置 SET NOCOUNT ON ，在结束时设置 SET NOCOUNT OFF 。无需在执行存储过程和触发器的每个语句后向客户端发送 DONE_IN_PROC 消息。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;29、尽量避免向客户端返回大数据量，若数据量过大，应该考虑相应需求是否合理。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, &amp;quot;font-size:16px;line-height:29.75px;background-color:#FFFFFF;\"&gt;30、尽量避免大事务操作，提高系统并发能力。&lt;/span&gt;', '2019-03-12 19:00:55', '2019-03-13 12:15:06', 'niuhao', 'MySQL 中 SQL 优化常用的 30 种方法，你会几种？！', '', 'MySQL 中 SQL 优化常用的 30 种方法，你会几种？！', 3, 1);
INSERT INTO `t_advertising` VALUES (105, 'c5c05b29ee4c', '&lt;span style=\"background-color:;\"&gt;Nginx&lt;/span&gt; \n&lt;blockquote&gt;\n	&lt;p&gt;\n		&lt;span style=\"background-color:;\"&gt;本项目是一个 Nginx 极简教程，目的在于帮助新手快速入门 Nginx。&lt;/span&gt; \n	&lt;/p&gt;\n	&lt;p&gt;\n		&lt;a href=\"https://github.com/dunwu/nginx-tutorial/blob/master/demos\"&gt;&lt;span style=\"background-color:;\"&gt;demos&lt;/span&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt; 目录中的示例模拟了工作中的一些常用实战场景，并且都可以通过脚本一键式启动，让您可以快速看到演示效果。&lt;/span&gt;&lt;img src=\"http://localhost:8081/js/kindeditor/plugins/emoticons/images/63.gif\" border=\"0\" alt=\"\" /&gt; \n	&lt;/p&gt;\n&lt;/blockquote&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#概述\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;概述&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;什么是 Nginx?&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;Nginx (engine x) 是一款轻量级的 Web 服务器 、反向代理服务器及电子邮件（IMAP/POP3）代理服务器。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;a href=\"https://raw.githubusercontent.com/dunwu/images/master/images/web/nginx/nginx.jpg\"&gt;&lt;img src=\"https://raw.githubusercontent.com/dunwu/images/master/images/web/nginx/nginx.jpg\" /&gt;&lt;/a&gt; &lt;br /&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;什么是反向代理？&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;反向代理（Reverse Proxy）方式是指以代理服务器来接受 internet 上的连接请求，然后将请求转发给内部网络上的服务器，并将从服务器上得到的结果返回给 internet 上请求连接的客户端，此时代理服务器对外就表现为一个反向代理服务器。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;a href=\"https://raw.githubusercontent.com/dunwu/images/master/images/web/reverse-proxy.png\"&gt;&lt;img src=\"https://raw.githubusercontent.com/dunwu/images/master/images/web/reverse-proxy.png\" /&gt;&lt;/a&gt; &lt;br /&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#安装与使用\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;安装与使用 &lt;/span&gt;&lt;a href=\"https://github.com/dunwu/nginx-tutorial#安装\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;安装&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;详细安装方法请参考：&lt;/span&gt;&lt;a href=\"https://github.com/dunwu/nginx-tutorial/blob/master/install-nginx.md\"&gt;&lt;span style=\"background-color:;\"&gt;Nginx 安装&lt;/span&gt;&lt;/a&gt; \n&lt;/p&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#使用\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;使用&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;nginx 的使用比较简单，就是几条命令。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;常用到的命令如下：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;nginx -s stop       快速关闭Nginx，可能不保存相关信息，并迅速终止web服务。\nnginx -s quit       平稳关闭Nginx，保存相关信息，有安排的结束web服务。\nnginx -s reload     因改变了Nginx相关配置，需要重新加载配置而重载。\nnginx -s reopen     重新打开日志文件。\nnginx -c filename   为 Nginx 指定一个配置文件，来代替缺省的。\nnginx -t            不运行，而仅仅测试配置文件。nginx 将检查配置文件的语法的正确性，并尝试打开配置文件中所引用到的文件。\nnginx -v            显示 nginx 的版本。\nnginx -V            显示 nginx 的版本，编译器版本和配置参数。&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;如果不想每次都敲命令，可以在 nginx 安装目录下新添一个启动批处理文件startup.bat，双击即可运行。内容如下：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;@echo off\nrem 如果启动前已经启动nginx并记录下pid文件，会kill指定进程\nnginx.exe -s stop\n\nrem 测试配置文件语法正确性\nnginx.exe -t -c conf/nginx.conf\n\nrem 显示版本信息\nnginx.exe -v\n\nrem 按照指定配置去启动nginx\nnginx.exe -c conf/nginx.conf&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;如果是运行在 Linux 下，写一个 shell 脚本，大同小异。&lt;/span&gt; \n&lt;/p&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#nginx-配置实战\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;nginx 配置实战&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;我始终认为，各种开发工具的配置还是结合实战来讲述，会让人更易理解。&lt;/span&gt; \n&lt;/p&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#http-反向代理配置\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;http 反向代理配置&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;我们先实现一个小目标：不考虑复杂的配置，仅仅是完成一个 http 反向代理。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;nginx.conf 配置文件如下： 注：conf / nginx.conf 是 nginx 的默认配置文件。你也可以使用 nginx -c 指定你的配置文件&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;#运行用户\n#user somebody;\n\n#启动进程,通常设置成和cpu的数量相等\nworker_processes  1;\n\n#全局错误日志\nerror_log  D:/Tools/nginx-1.10.1/logs/error.log;\nerror_log  D:/Tools/nginx-1.10.1/logs/notice.log  notice;\nerror_log  D:/Tools/nginx-1.10.1/logs/info.log  info;\n\n#PID文件，记录当前启动的nginx的进程ID\npid        D:/Tools/nginx-1.10.1/logs/nginx.pid;\n\n#工作模式及连接数上限\nevents {\n    worker_connections 1024;    #单个后台worker process进程的最大并发链接数\n}\n\n#设定http服务器，利用它的反向代理功能提供负载均衡支持\nhttp {\n    #设定mime类型(邮件支持类型),类型由mime.types文件定义\n    include       D:/Tools/nginx-1.10.1/conf/mime.types;\n    default_type  application/octet-stream;\n\n    #设定日志\n	log_format  main  \'[$remote_addr] - [$remote_user] [$time_local] \"$request\" \'\n                      \'$status $body_bytes_sent \"$http_referer\" \'\n                      \'\"$http_user_agent\" \"$http_x_forwarded_for\"\';\n\n    access_log    D:/Tools/nginx-1.10.1/logs/access.log main;\n    rewrite_log     on;\n\n    #sendfile 指令指定 nginx 是否调用 sendfile 函数（zero copy 方式）来输出文件，对于普通应用，\n    #必须设为 on,如果用来进行下载等应用磁盘IO重负载应用，可设置为 off，以平衡磁盘与网络I/O处理速度，降低系统的uptime.\n    sendfile        on;\n    #tcp_nopush     on;\n\n    #连接超时时间\n    keepalive_timeout  120;\n    tcp_nodelay        on;\n\n	#gzip压缩开关\n	#gzip  on;\n\n    #设定实际的服务器列表\n    upstream zp_server1{\n        server 127.0.0.1:8089;\n    }\n\n    #HTTP服务器\n    server {\n        #监听80端口，80端口是知名端口号，用于HTTP协议\n        listen       80;\n\n        #定义使用www.xx.com访问\n        server_name  www.helloworld.com;\n\n		#首页\n		index index.html\n\n		#指向webapp的目录\n		root D:\\01_Workspace\\Project\\github\\zp\\SpringNotes\\spring-security\\spring-shiro\\src\\main\\webapp;\n\n		#编码格式\n		charset utf-8;\n\n		#代理配置参数\n        proxy_connect_timeout 180;\n        proxy_send_timeout 180;\n        proxy_read_timeout 180;\n        proxy_set_header Host $host;\n        proxy_set_header X-Forwarder-For $remote_addr;\n\n        #反向代理的路径（和upstream绑定），location 后面设置映射的路径\n        location / {\n            proxy_pass http://zp_server1;\n        }\n\n        #静态文件，nginx自己处理\n        location ~ ^/(images|javascript|js|css|flash|media|static)/ {\n            root D:\\01_Workspace\\Project\\github\\zp\\SpringNotes\\spring-security\\spring-shiro\\src\\main\\webapp\\views;\n            #过期30天，静态文件不怎么更新，过期可以设大一点，如果频繁更新，则可以设置得小一点。\n            expires 30d;\n        }\n\n        #设定查看Nginx状态的地址\n        location /NginxStatus {\n            stub_status           on;\n            access_log            on;\n            auth_basic            \"NginxStatus\";\n            auth_basic_user_file  conf/htpasswd;\n        }\n\n        #禁止访问 .htxxx 文件\n        location ~ /\\.ht {\n            deny all;\n        }\n\n		#错误处理页面（可选择性配置）\n		#error_page   404              /404.html;\n		#error_page   500 502 503 504  /50x.html;\n        #location = /50x.html {\n        #    root   html;\n        #}\n    }\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;好了，让我们来试试吧：&lt;/span&gt; \n&lt;/p&gt;\n&lt;ol&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;启动 webapp，注意启动绑定的端口要和 nginx 中的 upstream 设置的端口保持一致。&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;更改 host：在 C:\\Windows\\System32\\drivers\\etc 目录下的 host 文件中添加一条 DNS 记录&lt;/span&gt; \n	&lt;/li&gt;\n&lt;/ol&gt;\n&lt;span style=\"background-color:;\"&gt;127.0.0.1 www.helloworld.com&lt;/span&gt; \n&lt;ol&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;启动前文中 startup.bat 的命令&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;在浏览器中访问 &lt;/span&gt;&lt;a href=\"http://www.helloworld.xn--com%2C%2C-ri1hi2ayve9sgnfr4qtgo7zjr48hrtvb1b8a./\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com，不出意外，已经可以访问了。&lt;/span&gt;&lt;/a&gt; \n	&lt;/li&gt;\n&lt;/ol&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#负载均衡配置\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;负载均衡配置&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;上一个例子中，代理仅仅指向一个服务器。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;但是，网站在实际运营过程中，多半都是有多台服务器运行着同样的 app，这时需要使用负载均衡来分流。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;nginx 也可以实现简单的负载均衡功能。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;假设这样一个应用场景：将应用部署在 192.168.1.11:80、192.168.1.12:80、192.168.1.13:80 三台 linux 环境的服务器上。网站域名叫 &lt;/span&gt;&lt;a href=\"http://www.helloworld.xn--com%2C-tt8fy44v/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com，公网&lt;/span&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt; IP 为 192.168.1.11。在公网 IP 所在的服务器上部署 nginx，对所有请求做负载均衡处理。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;nginx.conf 配置如下：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;http {\n     #设定mime类型,类型由mime.type文件定义\n    include       /etc/nginx/mime.types;\n    default_type  application/octet-stream;\n    #设定日志格式\n    access_log    /var/log/nginx/access.log;\n\n    #设定负载均衡的服务器列表\n    upstream load_balance_server {\n        #weigth参数表示权值，权值越高被分配到的几率越大\n        server 192.168.1.11:80   weight=5;\n        server 192.168.1.12:80   weight=1;\n        server 192.168.1.13:80   weight=6;\n    }\n\n   #HTTP服务器\n   server {\n        #侦听80端口\n        listen       80;\n\n        #定义使用www.xx.com访问\n        server_name  www.helloworld.com;\n\n        #对所有请求进行负载均衡请求\n        location / {\n            root        /root;                 #定义服务器的默认网站根目录位置\n            index       index.html index.htm;  #定义首页索引文件的名称\n            proxy_pass  http://load_balance_server ;#请求转向load_balance_server 定义的服务器列表\n\n            #以下是一些反向代理的配置(可选择性配置)\n            #proxy_redirect off;\n            proxy_set_header Host $host;\n            proxy_set_header X-Real-IP $remote_addr;\n            #后端的Web服务器可以通过X-Forwarded-For获取用户真实IP\n            proxy_set_header X-Forwarded-For $remote_addr;\n            proxy_connect_timeout 90;          #nginx跟后端服务器连接超时时间(代理连接超时)\n            proxy_send_timeout 90;             #后端服务器数据回传时间(代理发送超时)\n            proxy_read_timeout 90;             #连接成功后，后端服务器响应时间(代理接收超时)\n            proxy_buffer_size 4k;              #设置代理服务器（nginx）保存用户头信息的缓冲区大小\n            proxy_buffers 4 32k;               #proxy_buffers缓冲区，网页平均在32k以下的话，这样设置\n            proxy_busy_buffers_size 64k;       #高负荷下缓冲大小（proxy_buffers*2）\n            proxy_temp_file_write_size 64k;    #设定缓存文件夹大小，大于这个值，将从upstream服务器传\n\n            client_max_body_size 10m;          #允许客户端请求的最大单文件字节数\n            client_body_buffer_size 128k;      #缓冲区代理缓冲用户端请求的最大字节数\n        }\n    }\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;h3&gt;\n	&lt;span style=\"background-color:;\"&gt;网站有多个 webapp 的配置&lt;/span&gt; \n&lt;/h3&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;当一个网站功能越来越丰富时，往往需要将一些功能相对独立的模块剥离出来，独立维护。这样的话，通常，会有多个 webapp。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;举个例子：假如 &lt;/span&gt;&lt;a href=\"http://www.helloworld.com/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com&lt;/span&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt; 站点有好几个 webapp，finance（金融）、product（产品）、admin（用户中心）。访问这些应用的方式通过上下文(context)来进行区分:&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;a href=\"http://www.helloworld.com/finance/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com/finance/&lt;/span&gt;&lt;/a&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;a href=\"http://www.helloworld.com/product/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com/product/&lt;/span&gt;&lt;/a&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;a href=\"http://www.helloworld.com/admin/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com/admin/&lt;/span&gt;&lt;/a&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;我们知道，http 的默认端口号是 80，如果在一台服务器上同时启动这 3 个 webapp 应用，都用 80 端口，肯定是不成的。所以，这三个应用需要分别绑定不同的端口号。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;那么，问题来了，用户在实际访问 &lt;/span&gt;&lt;a href=\"http://www.helloworld.com/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com&lt;/span&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt; 站点时，访问不同 webapp，总不会还带着对应的端口号去访问吧。所以，你再次需要用到反向代理来做处理。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;配置也不难，来看看怎么做吧：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;http {\n	#此处省略一些基本配置\n\n	upstream product_server{\n		server www.helloworld.com:8081;\n	}\n\n	upstream admin_server{\n		server www.helloworld.com:8082;\n	}\n\n	upstream finance_server{\n		server www.helloworld.com:8083;\n	}\n\n	server {\n		#此处省略一些基本配置\n		#默认指向product的server\n		location / {\n			proxy_pass http://product_server;\n		}\n\n		location /product/{\n			proxy_pass http://product_server;\n		}\n\n		location /admin/ {\n			proxy_pass http://admin_server;\n		}\n\n		location /finance/ {\n			proxy_pass http://finance_server;\n		}\n	}\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;h3&gt;\n	&lt;span style=\"background-color:;\"&gt;https 反向代理配置&lt;/span&gt; \n&lt;/h3&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;一些对安全性要求比较高的站点，可能会使用 HTTPS（一种使用 ssl 通信标准的安全 HTTP 协议）。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;这里不科普 HTTP 协议和 SSL 标准。但是，使用 nginx 配置 https 需要知道几点：&lt;/span&gt; \n&lt;/p&gt;\n&lt;ul&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;HTTPS 的固定端口号是 443，不同于 HTTP 的 80 端口&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;SSL 标准需要引入安全证书，所以在 nginx.conf 中你需要指定证书和它对应的 key&lt;/span&gt; \n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;其他和 http 反向代理基本一样，只是在 Server 部分配置有些不同。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt; #HTTP服务器\n  server {\n      #监听443端口。443为知名端口号，主要用于HTTPS协议\n      listen       443 ssl;\n\n      #定义使用www.xx.com访问\n      server_name  www.helloworld.com;\n\n      #ssl证书文件位置(常见证书文件格式为：crt/pem)\n      ssl_certificate      cert.pem;\n      #ssl证书key位置\n      ssl_certificate_key  cert.key;\n\n      #ssl配置参数（选择性配置）\n      ssl_session_cache    shared:SSL:1m;\n      ssl_session_timeout  5m;\n      #数字签名，此处使用MD5\n      ssl_ciphers  HIGH:!aNULL:!MD5;\n      ssl_prefer_server_ciphers  on;\n\n      location / {\n          root   /root;\n          index  index.html index.htm;\n      }\n  }&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;h3&gt;\n	&lt;span style=\"background-color:;\"&gt;静态站点配置&lt;/span&gt; \n&lt;/h3&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;有时候，我们需要配置静态站点(即 html 文件和一堆静态资源)。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;举例来说：如果所有的静态资源都放在了 /app/dist 目录下，我们只需要在 nginx.conf 中指定首页以及这个站点的 host 即可。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;配置如下：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;worker_processes  1;\n\nevents {\n	worker_connections  1024;\n}\n\nhttp {\n    include       mime.types;\n    default_type  application/octet-stream;\n    sendfile        on;\n    keepalive_timeout  65;\n\n    gzip on;\n    gzip_types text/plain application/x-javascript text/css application/xml text/javascript application/javascript image/jpeg image/gif image/png;\n    gzip_vary on;\n\n    server {\n		listen       80;\n		server_name  static.zp.cn;\n\n		location / {\n			root /app/dist;\n			index index.html;\n			#转发任何请求到 index.html\n		}\n	}\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;然后，添加 HOST：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;127.0.0.1 static.zp.cn&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;此时，在本地浏览器访问 static.zp.cn ，就可以访问静态站点了。&lt;/span&gt; \n&lt;/p&gt;\n&lt;a href=\"https://github.com/dunwu/nginx-tutorial#搭建文件服务器\"&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt;搭建文件服务器&lt;/span&gt; \n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;有时候，团队需要归档一些数据或资料，那么文件服务器必不可少。使用 Nginx 可以非常快速便捷的搭建一个简易的文件服务。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;Nginx 中的配置要点：&lt;/span&gt; \n&lt;/p&gt;\n&lt;ul&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;将 autoindex 开启可以显示目录，默认不开启。&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;将 autoindex_exact_size 开启可以显示文件的大小。&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;将 autoindex_localtime 开启可以显示文件的修改时间。&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;root 用来设置开放为文件服务的根路径。&lt;/span&gt; \n	&lt;/li&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;charset 设置为 charset utf-8,gbk;，可以避免中文乱码问题（windows 服务器下设置后，依然乱码，本人暂时没有找到解决方法）。&lt;/span&gt; \n	&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;一个最简化的配置如下：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums\"&gt;autoindex on;# 显示目录\nautoindex_exact_size on;# 显示文件大小\nautoindex_localtime on;# 显示文件时间\n\nserver {\n    charset      utf-8,gbk; # windows 服务器下设置后，依然乱码，暂时无解\n    listen       9050 default_server;\n    listen       [::]:9050 default_server;\n    server_name  _;\n    root         /share/fs;\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;跨域解决方案&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;web 领域开发中，经常采用前后端分离模式。这种模式下，前端和后端分别是独立的 web 应用程序，例如：后端是 Java 程序，前端是 React 或 Vue 应用。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;各自独立的 web app 在互相访问时，势必存在跨域问题。解决跨域问题一般有两种思路：&lt;/span&gt; \n&lt;/p&gt;\n&lt;ol&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;CORS&lt;/span&gt; \n	&lt;/li&gt;\n&lt;/ol&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;在后端服务器设置 HTTP 响应头，把你需要运行访问的域名加入加入 Access-Control-Allow-Origin 中。&lt;/span&gt; \n&lt;/p&gt;\n&lt;ol&gt;\n	&lt;li&gt;\n		&lt;span style=\"background-color:;\"&gt;jsonp&lt;/span&gt; \n	&lt;/li&gt;\n&lt;/ol&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;把后端根据请求，构造 json 数据，并返回，前端用 jsonp 跨域。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;这两种思路，本文不展开讨论。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;需要说明的是，nginx 根据第一种思路，也提供了一种解决跨域的解决方案。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;举例：&lt;/span&gt;&lt;a href=\"http://www.helloworld.com/\"&gt;&lt;span style=\"background-color:;\"&gt;www.helloworld.com&lt;/span&gt;&lt;/a&gt;&lt;span style=\"background-color:;\"&gt; 网站是由一个前端 app ，一个后端 app 组成的。前端端口号为 9000， 后端端口号为 8080。&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;前端和后端如果使用 http 进行交互时，请求会被拒绝，因为存在跨域问题。来看看，nginx 是怎么解决的吧：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;首先，在 enable-cors.conf 文件中设置 cors ：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;# allow origin list\nset $ACAO \'*\';\n\n# set single origin\nif ($http_origin ~* (www.helloworld.com)$) {\n  set $ACAO $http_origin;\n}\n\nif ($cors = \"trueget\") {\n	add_header \'Access-Control-Allow-Origin\' \"$http_origin\";\n	add_header \'Access-Control-Allow-Credentials\' \'true\';\n	add_header \'Access-Control-Allow-Methods\' \'GET, POST, OPTIONS\';\n	add_header \'Access-Control-Allow-Headers\' \'DNT,X-Mx-ReqToken,Keep-Alive,User-Agent,X-Requested-With,If-Modified-Since,Cache-Control,Content-Type\';\n}\n\nif ($request_method = \'OPTIONS\') {\n  set $cors \"${cors}options\";\n}\n\nif ($request_method = \'GET\') {\n  set $cors \"${cors}get\";\n}\n\nif ($request_method = \'POST\') {\n  set $cors \"${cors}post\";\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;接下来，在你的服务器中 include enable-cors.conf 来引入跨域配置：&lt;/span&gt; \n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;# allow origin list\nset $ACAO \'*\';\n\n# set single origin\nif ($http_origin ~* (www.helloworld.com)$) {\n  set $ACAO $http_origin;\n}\n\nif ($cors = \"trueget\") {\n	add_header \'Access-Control-Allow-Origin\' \"$http_origin\";\n	add_header \'Access-Control-Allow-Credentials\' \'true\';\n	add_header \'Access-Control-Allow-Methods\' \'GET, POST, OPTIONS\';\n	add_header \'Access-Control-Allow-Headers\' \'DNT,X-Mx-ReqToken,Keep-Alive,User-Agent,X-Requested-With,If-Modified-Since,Cache-Control,Content-Type\';\n}\n\nif ($request_method = \'OPTIONS\') {\n  set $cors \"${cors}options\";\n}\n\nif ($request_method = \'GET\') {\n  set $cors \"${cors}get\";\n}\n\nif ($request_method = \'POST\') {\n  set $cors \"${cors}post\";\n}&lt;/pre&gt;\n&lt;p&gt;\n	&lt;span style=\"background-color:;\"&gt;到此，就完成了。&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;br /&gt;\n&lt;/p&gt;', '2019-03-13 10:29:21', '2019-03-13 11:50:48', 'niuhao', 'Nginx 极简教程', '', 'Nginx 极简教程', 1, 1);
INSERT INTO `t_advertising` VALUES (106, 'c5c05b29ee4c', '&lt;div&gt;\n	&lt;div&gt;\n		&lt;h3&gt;\n			1.查看磁盘信息\n		&lt;/h3&gt;\n		&lt;div&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;df -ah #磁盘分区占用情况\ndf -h #详细显示出各子文件的大小情况\ndu -sh #目录整体占用大小&lt;/pre&gt;\n		&lt;/div&gt;\n		&lt;h3&gt;\n			2.查看当前目录占用情况\n		&lt;/h3&gt;\n		&lt;div&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;#以根目录“/”为例，使用如下命令当前目录占用情况：\n cd /\n sudo du -sh *\ndu -ach *    #这个能看到当前目录下的所有文件占用磁盘大小和总大小\ndu -sh       #查看当前目录总大小\ndu -sh *     #查看所有子目录大小\ndu -sh | sort -nr 倒序查看文件大小&lt;/pre&gt;\n		&lt;/div&gt;\n		&lt;div&gt;\n		&lt;/div&gt;\n		&lt;h3&gt;\n			3.查看磁盘物理分区信息\n		&lt;/h3&gt;\n		&lt;div&gt;\n&lt;pre class=\"prettyprint linenums lang-bsh\"&gt;cat /proc/partitions&lt;/pre&gt;\n&lt;br /&gt;\n		&lt;/div&gt;\n		&lt;div&gt;\n			4.查看 20天前文件；ls -ltrh(查看权限)；\n		&lt;/div&gt;\n		&lt;div&gt;\n			find /root/data/springboot/ -mtime +20 -name *.log | xargs ls -ltrh\n		&lt;/div&gt;\n		&lt;div&gt;\n			找到并删除文件\n		&lt;/div&gt;\n		&lt;div&gt;\n			find /root/data/springboot -mtime +20 -name *.log | xargs rm -f\n		&lt;/div&gt;\n		&lt;div&gt;\n			find /root/data/springboot -mtime +20 -name *.log -delete\n		&lt;/div&gt;\n		&lt;div&gt;\n			5.查看内存信息\n		&lt;/div&gt;\n		&lt;div&gt;\n			#free\n		&lt;/div&gt;\n		&lt;div&gt;\n			total:总计物理内存的大小。\n		&lt;/div&gt;\n		&lt;div&gt;\n			used:已使用多大。\n		&lt;/div&gt;\n		&lt;div&gt;\n			free:可用有多少。\n		&lt;/div&gt;\n		&lt;div&gt;\n			Shared:多个进程共享的内存总额。\n		&lt;/div&gt;\n		&lt;div&gt;\n			Buffers/cached:磁盘缓存的大小。\n		&lt;/div&gt;\n		&lt;div&gt;\n			所以空闲内存=free+buffers+cached=total-used\n		&lt;/div&gt;\n		&lt;div&gt;\n			6.带宽信息\n		&lt;/div&gt;\n		&lt;div&gt;\n			1、服务器内部安装&amp;nbsp;iftop 流量监控工具：\n		&lt;/div&gt;\n	&lt;/div&gt;\n	&lt;div&gt;\n		&lt;h1&gt;\n			&lt;div&gt;\n				&lt;div&gt;\n					&lt;a href=\"https://api.aliyun.com/new#/tutorial?id=41330\" target=\"_blank\"&gt;试用&lt;/a&gt; \n				&lt;/div&gt;\n				&lt;div&gt;\n				&lt;/div&gt;\n				&lt;ol&gt;\n					&lt;li&gt;\n						yum install iftop-y\n					&lt;/li&gt;\n				&lt;/ol&gt;\n			&lt;/div&gt;\n			&lt;p&gt;\n				&amp;nbsp;&lt;a href=\"https://gw.alipayobjects.com/zos/onekb/uZvtEVjeKRSLrTECKHEH.jpg\"&gt;&lt;img alt=\"3.JPG\" src=\"https://gw.alipayobjects.com/zos/onekb/uZvtEVjeKRSLrTECKHEH.jpg\" title=\"\" /&gt;&lt;/a&gt; \n			&lt;/p&gt;\n			&lt;p&gt;\n				2、.服务器外网带宽被占满时，如果通过远程无法登陆，可通过阿里云终端管理进入到服务器内部，运行下面命令查看流量占用情况：\n			&lt;/p&gt;\n			&lt;div&gt;\n				&lt;div&gt;\n					&lt;a href=\"https://api.aliyun.com/new#/tutorial?id=41330\" target=\"_blank\"&gt;试用&lt;/a&gt; \n				&lt;/div&gt;\n				&lt;div&gt;\n				&lt;/div&gt;\n				&lt;ol&gt;\n					&lt;li&gt;\n						iftop-i eth1-P\n					&lt;/li&gt;\n					&lt;li&gt;\n						iftop-i eth0-P\n					&lt;/li&gt;\n				&lt;/ol&gt;\n			&lt;/div&gt;\n		&lt;/h1&gt;\n	&lt;/div&gt;\n	&lt;div&gt;\n		7.磁盘挂载\n	&lt;/div&gt;\n	&lt;div&gt;\n		mount /dev/vdb /disk2/\n	&lt;/div&gt;\n	&lt;div&gt;\n		&lt;img src=\"http://p.qlogo.cn/qqmail_head/ribyQVdM7tLH2AROFKVbFrKMP0FfY3nUAiakeVogs7DkT5QGzPBXDEqmAE2quPbocf3FyXh1QuKchtrfNhjADc9zy8YvzdkMW1/0\" border=\"0\" /&gt; \n	&lt;/div&gt;\n&lt;/div&gt;\n&lt;div&gt;\n	&lt;div&gt;\n		8.查看网络\n	&lt;/div&gt;\n	&lt;div&gt;\n		netstat -nptl\n	&lt;/div&gt;\n	&lt;div&gt;\n		#列出该进程的启动位置\n	&lt;/div&gt;\n	&lt;div&gt;\n		&lt;span sf=\"\" ui=\"\" text\',=\"\" arial,=\"\" \'pingfang=\"\" sc\',=\"\" \'hiragino=\"\" sans=\"\" gb\',=\"\" \'microsoft=\"\" yahei\',=\"\" \'wenquanyi=\"\" micro=\"\" hei\',=\"\" sans-serif,=\"\" simhei,=\"\" simsun;=\"\" font-size:=\"\" 14px;=\"\" color:=\"\" rgb(79,=\"\" 79,=\"\" 79);=\"\" background-color:=\"\" rgba(0,=\"\" 0,=\"\" 0);=\"\" font-weight:=\"\" normal;=\"\" font-style:=\"\" normal;text-decoration:=\"\" none;\'=\"\" style=\"font-weight: normal; font-style: normal;\"&gt;ll&amp;nbsp;/proc/1501&lt;/span&gt; \n	&lt;/div&gt;\n	&lt;div&gt;\n		ps -aux | grep java\n	&lt;/div&gt;\n	&lt;div&gt;\n		ps -aux | grep tomcat\n	&lt;/div&gt;\n	&lt;div&gt;\n		9.SSL证书申请之如何创建\n	&lt;/div&gt;\n	&lt;div&gt;\n		&lt;div&gt;\n			cp /opt/ssl /root/data/apache-tomcat-8.5.35/webapps/.well-known/pki-validation\n		&lt;/div&gt;\n		&lt;div&gt;\n			mv /root/fileauth.txt /root/.well-known/pki-validation\n		&lt;/div&gt;\n		&lt;div&gt;\n			cp -R ./. /root/.well-known/pki-validation\n		&lt;/div&gt;\n	&lt;/div&gt;\n&lt;/div&gt;', '2019-03-14 11:37:59', '2019-03-19 10:42:25', 'niuhao', 'linux常用服务器运维命令', '', 'linux常用服务器运维命令', 3, 1);
INSERT INTO `t_advertising` VALUES (107, 'c5c05b29ee4c', '&lt;h3&gt;\n	&lt;span&gt;一、查看磁盘物理分区信息&lt;/span&gt;\n&lt;/h3&gt;\n&lt;span&gt;使用如下命令查看磁盘分区信息：&lt;/span&gt;&lt;br /&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;cat /proc/partitions&lt;/pre&gt;\n&lt;br /&gt;\n&lt;span&gt;查看磁盘分区占用情况&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;使用如下命令查看磁盘分区占用情况：&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;df -ah&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;1&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;2&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;三、查看当前目录占用情况&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;以根目录“/”为例，使用如下命令当前目录占用情况：&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;&amp;nbsp;cd /&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;&amp;nbsp;sudo du -sh *&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;1&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;2&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;3&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;1.查看当前路径下，每个文件/夹占用空间大小：du -sh *&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;查看\"/usr/local/\"路径下，所有文件大小总和。只列出总和，不显示该路径下各子文件的大小。&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;du -sh /usr/local/&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;结果显示如下图：&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;如果要详细显示出各子文件的大小情况，去掉\"s\"选项即可，如下：&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;du -h /usr/local/&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;2.查看各分区占用情况&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;df -h&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;结果显示如下图：&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;查看总容量、已使用、未使用容量：df -hl&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;-h：以kb以上单位显示&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;-l：仅显示本地文件系统&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;du -ach * &amp;nbsp; &amp;nbsp;#这个能看到当前目录下的所有文件占用磁盘大小和总大小&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;du -sh &amp;nbsp; &amp;nbsp; &amp;nbsp; #查看当前目录总大小&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;du -sh * &amp;nbsp; &amp;nbsp; #查看所有子目录大小&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;du -sh | sort -nr 倒序查看文件大小&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;lsof | grep delete &amp;nbsp; &amp;nbsp;#如果怀疑删掉的数据还在占用磁盘空间试试这个&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;kill -9 pid &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; #结束掉进程就能释放磁盘空间了&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;在Linux下如何让文件夹下的文件让文件按大小排序？&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;有两种方法。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span&gt;方法一：# ls -lhSl 长格式显示，h human readable模式，大小单位为M,G等易读格式，S size按大小排序。&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;方法二：# du -h * | sort -n&lt;/span&gt;&lt;br /&gt;\n&lt;span&gt;当然您也可以结合管道查看文件夹内最大的几个文件或最小的几个文件, 再加上管道符号和head或者tail命令即可du -h * | sort -n|head du -h * | sort -n|tail&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;', '2019-03-14 11:40:21', '2019-03-14 13:53:17', 'niuhao', '常用服务器运维命令—查看磁盘占用情况', '', '常用服务器运维命令—查看磁盘占用情况', 1, 1);
INSERT INTO `t_advertising` VALUES (108, 'c5c05b29ee4c', '&lt;span style=\"line-height:1.5;\"&gt;1. 尽量在合适的场合使用单例&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 使用单例可以减轻加载的负担，缩短加载的时间，提高加载的效率，但并不是所有地方都适用于单例，简单来说，单例主要适用于以下三个方面：&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 第一，控制资源的使用，通过线程同步来控制资源的并发访问；&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 第二，控制实例的产生，以达到节约资源的目的；&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 第三，控制数据共享，在不建立直接关联的条件下，让多个不相关的进程或线程之间实现通信。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 2. 尽量避免随意使用静态变量&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 要知道，当某个对象被定义为stataic变量所引用，那么gc通常是不会回收这个对象所占有的内存，如&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Java代码&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;pre class=\"prettyprint linenums lang-js\"&gt;public class A{  \nstatic B b = new B();  \n}  &lt;/pre&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 此时静态变量b的生命周期与A类同步，如果A类不会卸载，那么b对象会常驻内存，直到程序终止。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 3. 尽量避免过多过常的创建Java对象&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 尽量避免在经常调用的方法，循环中new对象，由于系统不仅要花费时间来创建对象，而且还要花时间对这些对象进行垃圾回收和处理，在我们可以控制的范围内，最大限度的重用对象，最好能用基本的数据类型或数组来替代对象。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 4. 尽量使用final修饰符&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 带有final修饰符的类是不可派生的。在Java核心API中，有许多应用final的例子，例如java.lang.String。为String类指定final防止了使用者覆盖length()方法。另外，如果一个类是final的，则该类所有方法都是final的。Java编译器会寻找机会内联（inline）所有的final方法（这和具体的编译器实现有关）。此举能够使性能平均提高50%。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 5. 尽量使用局部变量&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 调用方法时传递的参数以及在调用中创建的临时变量都保存在栈（Stack）中，速度较快。其他变量，如静态变量、实例变量等，都在堆（Heap）中创建，速度较慢。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 6. 尽量处理好包装类型和基本类型两者的使用场所&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 虽然包装类型和基本类型在使用过程中是可以相互转换，但它们两者所产生的内存区域是完全不同的，基本类型数据产生和处理都在栈中处理，包装类型是对象，是在堆中产生实例。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 在集合类对象，有对象方面需要的处理适用包装类型，其他的处理提倡使用基本类型。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 7. 慎用synchronized，尽量减小synchronize的方法&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 都知道，实现同步是要很大的系统开销作为代价的，甚至可能造成死锁，所以尽量避免无谓的同步控制。synchronize方法被调用时，直接会把当前对象锁 了，在方法执行完之前其他线程无法调用当前对象的其他方法。所以synchronize的方法尽量小，并且应尽量使用方法同步代替代码块同步。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 8. 尽量使用StringBuilder和StringBuffer进行字符串连接&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 这个就不多讲了。&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 9. 尽量不要使用finalize方法&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 实际上，将资源清理放在finalize方法中完成是非常不好的选择，由于GC的工作量很大，尤其是回收Young代内存时，大都会引起应用程序暂停，所以再选择使用finalize方法进行资源清理，会导致GC负担更大，程序运行效率更差。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 10. 尽量使用基本数据类型代替对象&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; String str = \"hello\";&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 上面这种方式会创建一个“hello”字符串，而且JVM的字符缓存池还会缓存这个字符串；&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; String str = new String(\"hello\");&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 此时程序除创建字符串外，str所引用的String对象底层还包含一个char[]数组，这个char[]数组依次存放了h,e,l,l,o&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 11. 单线程应尽量使用HashMap、ArrayList&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; HashTable、Vector等使用了同步机制，降低了性能。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 12. 尽量合理的创建HashMap&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 当你要创建一个比较大的hashMap时，充分利用另一个构造函数&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; public HashMap(int initialCapacity, float loadFactor)&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 避免HashMap多次进行了hash重构,扩容是一件很耗费性能的事，在默认中initialCapacity只有16，而loadFactor是 0.75，需要多大的容量，你最好能准确的估计你所需要的最佳大小，同样的Hashtable，Vectors也是一样的道理。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 13. 尽量减少对变量的重复计算&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 如&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; for(int i=0;i&amp;lt;list.size();i++)&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 应该改为&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; for(int i=0,len=list.size();i&amp;lt;len;i++)&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 并且在循环中应该避免使用复杂的表达式，在循环中，循环条件会被反复计算，如果不使用复杂表达式，而使循环条件值不变的话，程序将会运行的更快。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 14. 尽量避免不必要的创建&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 如&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; A a = new A();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; if(i==1){list.add(a);}&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 应该改为&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; if(i==1){&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; A a = new A();&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; list.add(a);}&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 15. 尽量在finally块中释放资源&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 程序中使用到的资源应当被释放，以避免资源泄漏。这最好在finally块中去做。不管程序执行的结果如何，finally块总是会执行的，以确保资源的正确关闭。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 16. 尽量使用移位来代替\'a/b\'的操作&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; \"/\"是一个代价很高的操作，使用移位的操作将会更快和更有效&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 如&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a / 4;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a / 8;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 应该改为&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a &amp;gt;&amp;gt; 2;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a &amp;gt;&amp;gt; 3;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 但注意的是使用移位应添加注释，因为移位操作不直观，比较难理解&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 17.尽量使用移位来代替\'a*b\'的操作&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 同样的，对于\'*\'操作，使用移位的操作将会更快和更有效&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 如&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a * 4;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a * 8;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 应该改为&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a &amp;lt;&amp;lt; 2;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; int num = a &amp;lt;&amp;lt; 3;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 18. 尽量确定StringBuffer的容量&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; StringBuffer 的构造器会创建一个默认大小（通常是16）的字符数组。在使用中，如果超出这个大小，就会重新分配内存，创建一个更大的数组，并将原先的数组复制过来，再 丢弃旧的数组。在大多数情况下，你可以在创建 StringBuffer的时候指定大小，这样就避免了在容量不够的时候自动增长，以提高性能。&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 如：StringBuffer buffer = new StringBuffer(1000); &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 19. 尽量早释放无用对象的引用&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 大部分时，方法局部引用变量所引用的对象 会随着方法结束而变成垃圾，因此，大部分时候程序无需将局部，引用变量显式设为null。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 例如：&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Java代码&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Public void test(){ &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Object obj = new Object(); &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; …… &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Obj=null; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; } &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 上面这个就没必要了，随着方法test()的执行完成，程序中obj引用变量的作用域就结束了。但是如果是改成下面：&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; Java代码&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;Public void test(){  \nObject obj = new Object();  \n……  \nObj=null;  \n//执行耗时，耗内存操作；或调用耗时，耗内存的方法  \n……  \n}  &lt;/pre&gt;\n&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 这时候就有必要将obj赋值为null，可以尽早的释放对Object对象的引用。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 20. 尽量避免使用二维数组&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 二维数据占用的内存空间比一维数组多得多，大概10倍以上。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 21. 尽量避免使用split&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 除非是必须的，否则应该避免使用split，split由于支持正则表达式，所以效率比较低，如果是频繁的几十，几百万的调用将会耗费大量资源，如果确实需 要频繁的调用split，可以考虑使用apache的StringUtils.split(string,char)，频繁split的可以缓存结果。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 22. ArrayList &amp;amp; LinkedList&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 一 个是线性表，一个是链表，一句话，随机查询尽量使用ArrayList，ArrayList优于LinkedList，LinkedList还要移动指 针，添加删除的操作LinkedList优于ArrayList，ArrayList还要移动数据，不过这是理论性分析，事实未必如此，重要的是理解好2 者得数据结构，对症下药。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 23. 尽量使用System.arraycopy ()代替通过来循环复制数组&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; System.arraycopy() 要比通过循环来复制数组快的多&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 24. 尽量缓存经常使用的对象&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 尽可能将经常使用的对象进行缓存，可以使用数组，或HashMap的容器来进行缓存，但这种方式可能导致系统占用过多的缓存，性能下降，推荐可以使用一些第三方的开源工具，如EhCache，Oscache进行缓存，他们基本都实现了FIFO/FLU等缓存算法。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 25. 尽量避免非常大的内存分配&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 有时候问题不是由当时的堆状态造成的，而是因为分配失败造成的。分配的内存块都必须是连续的，而随着堆越来越满，找到较大的连续块越来越困难。&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 26. 慎用异常&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; &amp;nbsp;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=\"line-height:1.5;\"&gt; 当创建一个异常时，需要收集一个栈跟踪(stack track)，这个栈跟踪用于描述异常是在何处创建的。构建这些栈跟踪时需要为运行时栈做一份快照，正是这一部分开销很大。当需要创建一个 Exception 时，JVM 不得不说：先别动，我想就您现在的样子存一份快照，所以暂时停止入栈和出栈操作。栈跟踪不只包含运行时栈中的一两个元素，而是包含这个栈中的每一个元素。&lt;/span&gt;&lt;br /&gt;\n&lt;p&gt;\n	&lt;span style=\"line-height:1.5;\"&gt; 如 果您创建一个 Exception ，就得付出代价。好在捕获异常开销不大，因此可以使用 try-catch 将核心内容包起来。从技术上讲，您甚至可以随意地抛出异常，而不用花费很大的代价。招致性能损失的并不是 throw 操作——尽管在没有预先创建异常的情况下就抛出异常是有点不寻常。真正要花代价的是创建异常。幸运的是，好的编程习惯已教会我们，不应该不管三七二十一就 抛出异常。异常是为异常的情况而设计的，使用时也应该牢记这一原则。&lt;/span&gt;\n&lt;/p&gt;\n&lt;p&gt;\n	&lt;span style=\"line-height:1.5;\"&gt;27. 尽量重用对象&lt;br /&gt;\n&lt;br /&gt;\n特别是String对象的使用中，出现字符串连接情况时应使用StringBuffer代替，由于系统不仅要花时间生成对象，以后可能还需要花时间对这些对象进行垃圾回收和处理。因此生成过多的对象将会给程序的性能带来很大的影响。&lt;br /&gt;\n&lt;br /&gt;\n28. 不要重复初始化变量&lt;br /&gt;\n&lt;br /&gt;\n默认情况下，调用类的构造函数时，java会把变量初始化成确定的值，所有的对象被设置成null，整数变量设置成0，float和double变量设置成0.0，逻辑值设置成false。当一个类从另一个类派生时，这一点尤其应该注意，因为用new关键字创建一个对象时，构造函数链中的所有构造函数都会被自动调用。&lt;br /&gt;\n&lt;br /&gt;\n这里有个注意，给成员变量设置初始值但需要调用其他方法的时候，最好放在一个方法。比如initXXX()中，因为直接调用某方法赋值可能会因为类尚未初始化而抛空指针异常，如：public int state = this.getState()。&lt;br /&gt;\n&amp;nbsp;29. 在java+Oracle的应用系统开发中，java中内嵌的SQL语言应尽量使用大写形式，以减少Oracle解析器的解析负担。&lt;br /&gt;\n&amp;nbsp;30. 在java编程过程中，进行数据库连接，I/O流操作，在使用完毕后，及时关闭以释放资源。因为对这些大对象的操作会造成系统大的开销。&lt;br /&gt;\n&amp;nbsp;31. 过分的创建对象会消耗系统的大量内存，严重时，会导致内存泄漏，因此，保证过期的对象的及时回收具有重要意义。JVM的GC并非十分智能，因此建议在对象使用完毕后，手动设置成null。&lt;br /&gt;\n&amp;nbsp;32. 在使用同步机制时，应尽量使用方法同步代替代码块同步。&lt;br /&gt;\n&lt;br /&gt;\n33. 不要在循环中使用Try/Catch语句，应把Try/Catch放在循环最外层&lt;br /&gt;\n&lt;br /&gt;\nError是获取系统错误的类，或者说是虚拟机错误的类。不是所有的错误Exception都能获取到的，虚拟机报错Exception就获取不到，必须用Error获取。&lt;br /&gt;\n&lt;br /&gt;\n34. 通过StringBuffer的构造函数来设定它的初始化容量，可以明显提升性能&lt;br /&gt;\n&lt;br /&gt;\nStringBuffer的默认容量为16，当StringBuffer的容量达到最大容量时，它会将自身容量增加到当前的2倍+2，也就是2*n+2。无论何时，只要StringBuffer到达它的最大容量，它就不得不创建一个新的对象数组，然后复制旧的对象数组，这会浪费很多时间。所以给StringBuffer设置一个合理的初始化容量值，是很有必要的！&lt;br /&gt;\n&lt;br /&gt;\n35. 合理使用java.util.Vector&lt;br /&gt;\n&lt;br /&gt;\nVector与StringBuffer类似，每次扩展容量时，所有现有元素都要赋值到新的存储空间中。Vector的默认存储能力为10个元素，扩容加倍。&lt;br /&gt;\n&lt;br /&gt;\nvector.add(index,obj) 这个方法可以将元素obj插入到index位置，但index以及之后的元素依次都要向下移动一个位置（将其索引加 1）。 除非必要，否则对性能不利。同样规则适用于remove(int index)方法，移除此向量中指定位置的元素。将所有后续元素左移（将其索引减 1）。返回此向量中移除的元素。所以删除vector最后一个元素要比删除第1个元素开销低很多。删除所有元素最好用removeAllElements()方法。&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\n如果要删除vector里的一个元素可以使用 vector.remove(obj)；而不必自己检索元素位置，再删除，如int index = indexOf（obj）;vector.remove(index)。&lt;br /&gt;\n&lt;br /&gt;\n38. 不用new关键字创建对象的实例&lt;br /&gt;\n&lt;br /&gt;\n用new关键词创建类的实例时，构造函数链中的所有构造函数都会被自动调用。但如果一个对象实现了Cloneable接口，我们可以调用它的clone()方法。clone()方法不会调用任何类构造函数。&lt;br /&gt;\n&lt;br /&gt;\n下面是Factory模式的一个典型实现：&lt;br /&gt;\n&lt;br /&gt;\npublic static Credit getNewCredit()&lt;br /&gt;\n{&lt;br /&gt;\nreturn new Credit();&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n改进后的代码使用clone()方法：&lt;br /&gt;\n&lt;br /&gt;\nprivate static Credit BaseCredit = new Credit();&lt;br /&gt;\npublic static Credit getNewCredit()&lt;br /&gt;\n{&lt;br /&gt;\nreturn (Credit)BaseCredit.clone();&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n9. 不要将数组声明为：public static final&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;40. HaspMap的遍历：&lt;br /&gt;\n&lt;br /&gt;\nMap&amp;lt;String, String[]&amp;gt; paraMap = new HashMap&amp;lt;String, String[]&amp;gt;();&lt;br /&gt;\nfor( Entry&amp;lt;String, String[]&amp;gt; entry : paraMap.entrySet() )&lt;br /&gt;\n{&lt;br /&gt;\nString appFieldDefId = entry.getKey();&lt;br /&gt;\nString[] values = entry.getValue();&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n利用散列值取出相应的Entry做比较得到结果，取得entry的值之后直接取key和value。&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;41. array(数组)和ArrayList的使用&lt;br /&gt;\n&lt;br /&gt;\narray 数组效率最高，但容量固定，无法动态改变，ArrayList容量可以动态增长，但牺牲了效率。&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;42. 单线程应尽量使用 HashMap, ArrayList,除非必要，否则不推荐使用HashTable,Vector，它们使用了同步机制，而降低了性能。&lt;br /&gt;\n&lt;br /&gt;\n43. StringBuffer,StringBuilder的区别在于&lt;br /&gt;\n&lt;br /&gt;\njava.lang.StringBuffer 线程安全的可变字符序列。一个类似于String的字符串缓冲区，但不能修改。StringBuilder与该类相比，通常应该优先使用StringBuilder类，因为它支持所有相同的操作，但由于它不执行同步，所以速度更快。&lt;br /&gt;\n&lt;br /&gt;\n为了获得更好的性能，在构造StringBuffer或StringBuilder时应尽量指定她的容量。当然如果不超过16个字符时就不用了。 相同情况下，使用StringBuilder比使用StringBuffer仅能获得10%~15%的性能提升，但却要冒多线程不安全的风险。综合考虑还是建议使用StringBuffer。&lt;br /&gt;\n&lt;br /&gt;\n44. 尽量使用基本数据类型代替对象。&lt;br /&gt;\n&lt;br /&gt;\n45. 使用具体类比使用接口效率高，但结构弹性降低了，但现代IDE都可以解决这个问题。&lt;br /&gt;\n&lt;br /&gt;\n46. 考虑使用静态方法，如果你没有必要去访问对象的外部，那么就使你的方法成为静态方法。它会被更快地调用，因为它不需要一个虚拟函数导向表。这同时也是一个很好的实践，因为它告诉你如何区分方法的性质，调用这个方法不会改变对象的状态。&lt;br /&gt;\n&lt;br /&gt;\n47. 应尽可能避免使用内在的GET,SET方法。&lt;br /&gt;\n&lt;br /&gt;\n48.避免枚举，浮点数的使用。&lt;br /&gt;\n&lt;br /&gt;\n以下举几个实用优化的例子：&lt;br /&gt;\n&lt;br /&gt;\n一、避免在循环条件中使用复杂表达式&lt;br /&gt;\n&lt;br /&gt;\n在不做编译优化的情况下，在循环中，循环条件会被反复计算，如果不使用复杂表达式，而使循环条件值不变的话，程序将会运行的更快。例子：&lt;br /&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;import java.util.Vector;\nclass CEL {\nvoid method (Vector vector) {\nfor (int i = 0; i &amp;lt; vector.size (); i++) // Violation\n; // ...\n}\n}&lt;/pre&gt;\n&lt;br /&gt;\n更正：&lt;br /&gt;\n&lt;br /&gt;\nclass CEL_fixed {&lt;br /&gt;\nvoid method (Vector vector) {&lt;br /&gt;\nint size = vector.size ()&lt;br /&gt;\nfor (int i = 0; i &amp;lt; size; i++)&lt;br /&gt;\n; // ...&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n二、为\'Vectors\' 和 \'Hashtables\'定义初始大小&lt;br /&gt;\n&lt;br /&gt;\nJVM为Vector扩充大小的时候需要重新创建一个更大的数组，将原原先数组中的内容复制过来，最后，原先的数组再被回收。可见Vector容量的扩大是一个颇费时间的事。&lt;br /&gt;\n&lt;br /&gt;\n通常，默认的10个元素大小是不够的。你最好能准确的估计你所需要的最佳大小。例子：&lt;br /&gt;\n&lt;pre class=\"prettyprint linenums lang-java\"&gt;import java.util.Vector;\npublic class DIC {\npublic void addObjects (Object[] o) {\n// if length &amp;gt; 10, Vector needs to expand\nfor (int i = 0; i&amp;lt; o.length;i++) {\nv.add(o); // capacity before it can add more elements.\n}\n}\npublic Vector v = new Vector(); // no initialCapacity.\n}&lt;/pre&gt;\n&lt;br /&gt;\n更正：&lt;br /&gt;\n&lt;br /&gt;\n自己设定初始大小。&lt;br /&gt;\n&lt;br /&gt;\npublic Vector v = new Vector(20);&lt;br /&gt;\npublic Hashtable hash = new Hashtable(10);&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;三、在finally块中关闭Stream&lt;br /&gt;\n&lt;br /&gt;\n程序中使用到的资源应当被释放，以避免资源泄漏。这最好在finally块中去做。不管程序执行的结果如何，finally块总是会执行的，以确保资源的正确关闭。&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;四、使用\'System.arraycopy ()\'代替通过来循环复制数组&lt;br /&gt;\n&lt;br /&gt;\n例子：&lt;br /&gt;\n&lt;br /&gt;\npublic class IRB&lt;br /&gt;\n{&lt;br /&gt;\nvoid method () {&lt;br /&gt;\nint[] array1 = new int [100];&lt;br /&gt;\nfor (int i = 0; i &amp;lt; array1.length; i++) {&lt;br /&gt;\narray1 [i] = i;&lt;br /&gt;\n}&lt;br /&gt;\nint[] array2 = new int [100];&lt;br /&gt;\nfor (int i = 0; i &amp;lt; array2.length; i++) {&lt;br /&gt;\narray2 [i] = array1 [i]; // Violation&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n更正：&lt;br /&gt;\n&lt;br /&gt;\n&lt;br /&gt;\npublic class IRB&lt;br /&gt;\n{&lt;br /&gt;\nvoid method () {&lt;br /&gt;\nint[] array1 = new int [100];&lt;br /&gt;\nfor (int i = 0; i &amp;lt; array1.length; i++) {&lt;br /&gt;\narray1 [i] = i;&lt;br /&gt;\n}&lt;br /&gt;\nint[] array2 = new int [100];&lt;br /&gt;\nSystem.arraycopy(array1, 0, array2, 0, 100);&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n五、让访问实例内变量的getter/setter方法变成”final”&lt;br /&gt;\n&lt;br /&gt;\n简单的getter/setter方法应该被置成final，这会告诉编译器，这个方法不会被重载，所以，可以变成”inlined”,例子：&lt;br /&gt;\n&lt;br /&gt;\nclass MAF {&lt;br /&gt;\npublic void setSize (int size) {&lt;br /&gt;\n_size = size;&lt;br /&gt;\n}&lt;br /&gt;\nprivate int _size;&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n更正：&lt;br /&gt;\n&lt;br /&gt;\nclass DAF_fixed {&lt;br /&gt;\nfinal public void setSize (int size) {&lt;br /&gt;\n_size = size;&lt;br /&gt;\n}&lt;br /&gt;\nprivate int _size;&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n六、对于常量字符串，用\'String\' 代替 \'StringBuffer\'&lt;br /&gt;\n&lt;br /&gt;\n常量字符串并不需要动态改变长度。&lt;br /&gt;\n&lt;br /&gt;\n例子：&lt;br /&gt;\n&lt;br /&gt;\npublic class USC {&lt;br /&gt;\nString method () {&lt;br /&gt;\nStringBuffer s = new StringBuffer (\"Hello\");&lt;br /&gt;\nString t = s + \"World!\";&lt;br /&gt;\nreturn t;&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n更正：把StringBuffer换成String，如果确定这个String不会再变的话，这将会减少运行开销提高性能。&lt;br /&gt;\n&lt;br /&gt;\n七、在字符串相加的时候，使用 \' \' 代替 \" \"，如果该字符串只有一个字符的话&lt;br /&gt;\n例子：&lt;br /&gt;\n&lt;br /&gt;\npublic class STR {&lt;br /&gt;\npublic void method(String s) {&lt;br /&gt;\nString string = s + \"d\" // violation.&lt;br /&gt;\nstring = \"abc\" + \"d\" // violation.&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n更正：&lt;br /&gt;\n&lt;br /&gt;\n将一个字符的字符串替换成\' \'&lt;br /&gt;\n&lt;br /&gt;\npublic class STR {&lt;br /&gt;\npublic void method(String s) {&lt;br /&gt;\nString string = s + \'d\'&lt;br /&gt;\nstring = \"abc\" + \'d\'&lt;br /&gt;\n}&lt;br /&gt;\n}&lt;br /&gt;\n&lt;br /&gt;\n以上仅是Java方面编程时的性能优化，性能优化大部分都是在时间、效率、代码结构层次等方面的权衡，各有利弊，不要把上面内容当成教条，或许有些对我们实际工作适用，有些不适用，还望根据实际工作场景进行取舍，活学活用，变通为宜。&lt;br /&gt;\n	&lt;div&gt;\n		&lt;br /&gt;\n	&lt;/div&gt;\n&lt;br /&gt;\n&lt;/span&gt;\n&lt;/p&gt;', '2019-03-15 15:18:09', '2019-03-15 15:25:37', 'niuhao', 'java良好的编码习惯', '', 'java良好的编码习惯', 4, 1);
INSERT INTO `t_advertising` VALUES (110, 'c5c05b29ee4c', '&lt;h1 style=\"font-size:28px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;span&gt;开发工具&lt;/span&gt;\n&lt;/h1&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	不知道有多少”老”程序员还在使用 Eclipse，这些程序员们要不就是因循守旧，要不就是根本就不知道其他好的开发工具的存在，Eclipse 吃内存卡顿的现象以及各种偶然莫名异常的出现，都告知我们是时候寻找新的开发工具了。\n&lt;/p&gt;\n&lt;h3 style=\"font-size:22px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t5\"&gt;&lt;/a&gt;&lt;span&gt;更换 IDE&lt;/span&gt;\n&lt;/h3&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	根本就不想多解释要换什么样的 IDE，如果你想成为一个优秀的 Java 程序员，请更换 IntelliJ IDEA。使用 IDEA 的好处，请搜索谷歌。\n&lt;/p&gt;\n&lt;h3 style=\"font-size:22px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t6\"&gt;&lt;/a&gt;&lt;span&gt;别告诉我快捷键不好用&lt;/span&gt;\n&lt;/h3&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	更换 IDE 不在我本文的重点内容中，所以不想用太多的篇幅去写为什么更换IDE。在这里，我只能告诉你，更换 IDE 只为了更好、更快的写好 Java 代码。原因略。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	别告诉我快捷键不好用，请尝试新事物。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;img alt=\"\" class=\"has\" height=\"1\" src=\"https://img-blog.csdnimg.cn/20181222100540838.gif\" width=\"1\" /&gt;\n&lt;/p&gt;\n&lt;h1 style=\"font-size:28px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t7\"&gt;&lt;/a&gt;&lt;span&gt;bean&lt;/span&gt;\n&lt;/h1&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	bean 使我们使用最多的模型之一，我将以大篇幅去讲解 bean，希望读者好好体会。\n&lt;/p&gt;\n&lt;h3 style=\"font-size:22px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t8\"&gt;&lt;/a&gt;&lt;span&gt;domain 包名&lt;/span&gt;\n&lt;/h3&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	根据很多 Java 程序员的”经验”来看，一个数据库表则对应着一个 domain 对象，所以很多程序员在写代码时，包名则使用：com.xxx.domain ，这样写好像已经成为了行业的一种约束，数据库映射对象就应该是 domain。但是你错了，domain 是一个领域对象，往往我们再做传统 Java 软件 Web 开发中，这些 domain 都是贫血模型，是没有行为的，或是没有足够的领域模型的行为的，所以，以这个理论来讲，这些 domain 都应该是一个普通的 entity 对象，并非领域对象，所以请把包名改为:com.xxx.entity。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	如果你还不理解我说的话，请看一下 Vaughn Vernon 出的一本叫做《IMPLEMENTING DOMAIN-DRIVEN DESIGN》(实现领域驱动设计)这本书，书中讲解了贫血模型与领域模型的区别，相信你会受益匪浅。\n&lt;/p&gt;\n&lt;h3 style=\"font-size:22px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t9\"&gt;&lt;/a&gt;&lt;span&gt;DTO&lt;/span&gt;\n&lt;/h3&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	数据传输我们应该使用 DTO 对象作为传输对象，这是我们所约定的，因为很长时间我一直都在做移动端 API 设计的工作，有很多人告诉我，他们认为只有给手机端传输数据的时候(input or output)，这些对象成为 DTO 对象。请注意！这种理解是错误的，只要是用于网络传输的对象，我们都认为他们可以当做是 DTO 对象，比如电商平台中，用户进行下单，下单后的数据，订单会发到 OMS 或者 ERP 系统，这些对接的返回值以及入参也叫 DTO 对象。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	我们约定某对象如果是 DTO 对象，就将名称改为 XXDTO，比如订单下发OMS：OMSOrderInputDTO。\n&lt;/p&gt;\n&lt;h3 style=\"font-size:22px;font-family:&amp;quot;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;a name=\"t10\"&gt;&lt;/a&gt;&lt;span&gt;DTO 转化&lt;/span&gt;\n&lt;/h3&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	正如我们所知，DTO 为系统与外界交互的模型对象，那么肯定会有一个步骤是将 DTO 对象转化为 BO 对象或者是普通的 entity 对象，让 service 层去处理。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;span style=\"font-weight:700;\"&gt;场景&lt;/span&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	比如添加会员操作，由于用于演示，我只考虑用户的一些简单数据，当后台管理员点击添加用户时，只需要传过来用户的姓名和年龄就可以了，后端接受到数据后，将添加创建时间和更新时间和默认密码三个字段，然后保存数据库。\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@RequestMapping&lt;/span&gt;(&lt;span class=\"hljs-string\" style=\"color:#99CC99;\"&gt;\"/v1/api/user\"&lt;/span&gt;)\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@RestController&lt;/span&gt;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;public&lt;/span&gt;&amp;nbsp;&lt;span class=\"hljs-class\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;class&lt;/span&gt;&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;UserApi&lt;/span&gt;&amp;nbsp;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@Autowired&lt;/span&gt;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;private&lt;/span&gt;&amp;nbsp;UserService&amp;nbsp;userService;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@PostMapping&lt;/span&gt;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-function\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;public&lt;/span&gt;&amp;nbsp;User&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;addUser&lt;/span&gt;&lt;span class=\"hljs-params\" style=\"color:#F99157;\"&gt;(UserInputDTO&amp;nbsp;userInputDTO)&lt;/span&gt;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;new&lt;/span&gt;&amp;nbsp;User();\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;user.setUsername(userInputDTO.getUsername());\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;user.setAge(userInputDTO.getAge());\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;return&lt;/span&gt;&amp;nbsp;userService.addUser(user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				}\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	我们只关注一下上述代码中的转化代码，其他内容请忽略：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;new&lt;/span&gt;&amp;nbsp;User();\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				user.setUsername(userInputDTO.getUsername());\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				user.setAge(userInputDTO.getAge());\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;span style=\"font-weight:700;\"&gt;请使用工具&lt;/span&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	上边的代码，从逻辑上讲，是没有问题的，只是这种写法让我很厌烦，例子中只有两个字段，如果有 20 个字段，我们要如何做呢？ 一个一个进行 set 数据吗？当然，如果你这么做了，肯定不会有什么问题，但是，这肯定不是一个最优的做法。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	网上有很多工具，支持浅拷贝或深拷贝的 Utils。举个例子，我们可以使用 org.springframework.beans.BeanUtils#copyProperties 对代码进行重构和优化：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@PostMapping&lt;/span&gt;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-function\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;public&lt;/span&gt;&amp;nbsp;User&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;addUser&lt;/span&gt;&lt;span class=\"hljs-params\" style=\"color:#F99157;\"&gt;(UserInputDTO&amp;nbsp;userInputDTO)&lt;/span&gt;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;new&lt;/span&gt;&amp;nbsp;User();\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;BeanUtils.copyProperties(userInputDTO,user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;return&lt;/span&gt;&amp;nbsp;userService.addUser(user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				}\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	BeanUtils.copyProperties 是一个浅拷贝方法，复制属性时，我们只需要把 DTO 对象和要转化的对象两个的属性值设置为一样的名称，并且保证一样的类型就可以了。如果你在做 DTO 转化的时候一直使用 set 进行属性赋值，那么请尝试这种方式简化代码，让代码更加清晰!\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;span style=\"font-weight:700;\"&gt;转化的语义&lt;/span&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	上边的转化过程，读者看后肯定觉得优雅很多，但是我们再写 Java 代码时，更多的需要考虑语义的操作，再看上边的代码：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;new&lt;/span&gt;&amp;nbsp;User();\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				BeanUtils.copyProperties(userInputDTO,user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	虽然这段代码很好的简化和优化了代码，但是他的语义是有问题的，我们需要提现一个转化过程才好，所以代码改成如下：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-meta\" style=\"color:#F99157;\"&gt;@PostMapping&lt;/span&gt;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&lt;span class=\"hljs-function\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;public&lt;/span&gt;&amp;nbsp;User&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;addUser&lt;/span&gt;&lt;span class=\"hljs-params\" style=\"color:#F99157;\"&gt;(UserInputDTO&amp;nbsp;userInputDTO)&lt;/span&gt;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;convertFor(userInputDTO);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;return&lt;/span&gt;&amp;nbsp;userService.addUser(user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;}\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&lt;span class=\"hljs-function\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;private&lt;/span&gt;&amp;nbsp;User&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;convertFor&lt;/span&gt;&lt;span class=\"hljs-params\" style=\"color:#F99157;\"&gt;(UserInputDTO&amp;nbsp;userInputDTO)&lt;/span&gt;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;new&lt;/span&gt;&amp;nbsp;User();\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;BeanUtils.copyProperties(userInputDTO,user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;return&lt;/span&gt;&amp;nbsp;user;\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;}\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	这是一个更好的语义写法，虽然他麻烦了些，但是可读性大大增加了，在写代码时，我们应该尽量把语义层次差不多的放到一个方法中，比如：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				User&amp;nbsp;user&amp;nbsp;=&amp;nbsp;convertFor(userInputDTO);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;return&lt;/span&gt;&amp;nbsp;userService.addUser(user);\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n&lt;/ol&gt;\n&lt;/pre&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	这两段代码都没有暴露实现，都是在讲如何在同一个方法中，做一组相同层次的语义操作，而不是暴露具体的实现。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	如上所述，是一种重构方式，读者可以参考 Martin Fowler 的《Refactoring Imporving the Design of Existing Code》(重构 改善既有代码的设计) 这本书中的 Extract Method 重构方式。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	&lt;span style=\"font-weight:700;\"&gt;抽象接口定义&lt;/span&gt;\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	当实际工作中，完成了几个 API 的 DTO 转化时，我们会发现，这样的操作有很多很多，那么应该定义好一个接口，让所有这样的操作都有规则的进行。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	如果接口被定义以后，那么 convertFor 这个方法的语义将产生变化，它将是一个实现类。\n&lt;/p&gt;\n&lt;p style=\"font-family:&amp;quot;font-size:16px;color:#4F4F4F;background-color:#FFFFFF;\"&gt;\n	看一下抽象后的接口：\n&lt;/p&gt;\n&lt;pre class=\"has\"&gt;\n&lt;ol class=\"hljs-ln\" style=\"font-family:&amp;quot;font-size:16px;\"&gt;\n	\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;public&lt;/span&gt;&amp;nbsp;&lt;span class=\"hljs-class\"&gt;&lt;span class=\"hljs-keyword\" style=\"color:#CC99CC;\"&gt;interface&lt;/span&gt;&amp;nbsp;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;DTOConvert&lt;/span&gt;&amp;lt;&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;S&lt;/span&gt;,&lt;span class=\"hljs-title\" style=\"color:#6699CC;\"&gt;T&lt;/span&gt;&amp;gt;&amp;nbsp;&lt;/span&gt;{\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n	&lt;/li&gt;\n\n	&lt;li&gt;\n		\n		&lt;div class=\"hljs-ln-numbers\" style=\"margin:0px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line hljs-ln-n\" style=\"margin:0px;padding:0px 8px 0px 0px;text-align:right;\"&gt;\n				\n			&lt;/div&gt;\n\n		&lt;/div&gt;\n\n		&lt;div class=\"hljs-ln-code\" style=\"margin:0px 0px 0px 8px;padding:0px;font-family:&amp;quot;font-size:14px;\"&gt;\n			\n			&lt;div class=\"hljs-ln-line\" style=\"margin:0px;padding:0px;\"&gt;\n				&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span class=\"hljs-functio